DROP TABLE IF EXISTS `payments`;
CREATE TABLE `payments` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `atributes` text,
  `status` tinyint(4) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `products`;
CREATE TABLE `products` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `description` tinytext,
  `title` varchar(255) DEFAULT NULL,
  `brief` mediumtext,
  `price` float unsigned DEFAULT NULL,
  `price_sale` float unsigned DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `content` longtext,
  `modified` datetime DEFAULT NULL,
  `createdId` int(11) DEFAULT NULL,
  `modifiedId` int(11) DEFAULT NULL,
  `modifiedIds` varchar(255) DEFAULT NULL,
  `status` tinyint(3) unsigned DEFAULT NULL,
  `recommend` tinyint(4) DEFAULT NULL,
  `category_ids` varchar(300) DEFAULT NULL,
  `tag_ids` varchar(300) DEFAULT NULL,
  `outstock` tinyint(4) DEFAULT NULL,
  `slug` varchar(255) DEFAULT NULL,
  `ordering` int(11) DEFAULT NULL,
  `hot` tinyint(4) DEFAULT NULL,
  `sale` tinyint(3) unsigned DEFAULT NULL,
  `new` tinyint(3) unsigned DEFAULT NULL,
  `views` float DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

INSERT INTO `products` (`id`, `name`, `description`, `title`, `brief`, `price`, `price_sale`, `image`, `created`, `content`, `modified`, `createdId`, `modifiedId`, `modifiedIds`, `status`, `recommend`, `category_ids`, `tag_ids`, `outstock`, `slug`, `ordering`, `hot`, `sale`, `new`, `views`) VALUES
(1,	'Nấm linh chi đỏ Hàn Quốc',	'Nấm linh chi đỏ Hàn Quốc được xếp vào loại siêu thượng phẩm, giúp giải độc gan, hỗ trợ dạ dày, phòng chống ung thư và ngăn cản lão hóa.',	'Nấm linh chi đỏ Hàn Quốc phòng chống ung thư, ngăn ngừa lão hóa.',	'Nấm linh chi đỏ Hàn Quốc được xếp vào loại siêu thượng phẩm. Chúng giúp phòng chống ung thư, hỗ trợ hệ tiêu hóa, giải độc gan, tăng cường hệ miễn dịch…',	1300,	1200,	'cd8d1-nam-linh-chi-do-han-quoc.png',	'2018-04-12 14:28:08',	'<p><strong>Nấm linh chi đỏ Hàn Quốc</strong> nói riêng, các loại nấm linh chi nói chung thuộc họ nấm lim. Nấm linh chi đỏ Hàn Quốc được xếp vào loại siêu thượng phẩm. Từ hàng ngàn năm nay nấm linh chi đã được sử dụng trong việc bồi bổ sức khỏe và hỗ trợ chữa bệnh.</p>\r\n<p>Hiện có 6 loại nấm linh chi phổ biến. Đó là nấm linh chi vàng,đen, trắng, tím, xanh, đỏ. Tuy nhiên <strong>nấm linh chi đỏ</strong> là loại có dược tính mạnh nhất, được sử dụng phổ biến nhất. Nấm linh chi đỏ là loại thân gỗ. Nấm non trên mặt có màu đỏ bóng, dưới có màu trắng. Khi trưởng thành nấm linh chi có màu nâu bám trên mặt.</p>\r\n<h3>Tác dụng của nấm linh chi đỏ Hàn Quốc:</h3>\r\n<ul>\r\n<li>Phòng chống bênh ung thư.</li>\r\n<li>Phòng và hỗ trợ bênh tiểu đường.</li>\r\n<li>Chống lão hóa, làm đẹp da.</li>\r\n<li>Nấm linh chi có tác dụng giảm căng thẳng, mệt mỏi, tránh đau đầu.</li>\r\n<li>Giúp tuần hoàn máu tốt, chống xơ vữa động mạch.</li>\r\n<li>Thúc đẩy hệ tiêu hóa, chống táo bón.</li>\r\n<li>Giúp gan bài tiết tốt, bảo vệ gan.</li>\r\n<li>Tăng cường hệ miễn dịch.</li>\r\n</ul>\r\n<h3>Cách sử dụng nấm linh chi hiệu quả:</h3>\r\n<p>Có khá nhiều cách sử dụng nấm linh chi hiệu quả cho sức khỏe của bạn.</p>\r\n<p><strong>Cách 1: Uống nước linh chi thay nước.</strong></p>\r\n<p>Lần 1: Đun sôi 2 đến 3 phút hỗn hợp (50g linh chi + 1 lít nước) rồi tắt lửa. Sau 5 phút tắt bếp thì đun lửa nhỏ trong 30 phút. Khi nước cạn còn khoảng 0,8 lít thì được nước đầu tiên.</p>\r\n<p>Lần 2: Bạn cắt nhỏ phần bã linh chi đã nấu ở lần 1, rồi nấu như lần 1 để được nước thứ 2, và thứ 3.</p>\r\n<p>Số nước nấu trong 3 lần bạn có thể để tủ uống thay nước. Bạn cũng có thể cho thêm cam thảo hoặc táo tàu để giúp dễ uống hơn.</p>\r\n<p><strong>Cách 2: Uống dạng trà.</strong></p>\r\n<p>Nghiền nấm linh chi thành bột. Pha nấm linh chi như trà với nước sôi, và uống hết cả bã.</p>\r\n<p>Có thể bạn thấy khó uống hơn, nhưng theo  lời khuyên của các nahf khoa học thì đây là cách tốt nhất cho sức khỏe của bạn.</p>\r\n<p><strong>Cách 3: Ngâm rượu.</strong></p>\r\n<p>Ngâm 200g nấm linh chi khô (để nguyên hoặc thái lát) + 2 lít rượu (khoảng 39 độ). Khoảng 30 ngày thì có thể sử dụng. Bạn nên sử dụng sau bữa ăn tối, mỗi lần uống 1 hoặc 2 ly nhỏ.</p>\r\n<p>Rượu linh chi ngâm càng lâu thì càng tốt.</p>\r\n<p><strong>Cách 4: Làm đẹp.</strong></p>\r\n<ul>\r\n<li>Bột nấm linh chi trộn với mật ong có thể làm mặt nạ dưỡng da.</li>\r\n<li>Khi nấu nước uống xong, bã nấm linh chi bạn có thể đun lại làm nước tắm, giúp da hồng hào.</li>\r\n</ul>\r\n<p>Trên đây là một số cách sử dụng nấm linh chi hiệu quả. Hi vọng chúng tôi đã có thể giúp bạn hiểu hơn phần nào về tác dụng của nấm linh chi.</p>\r\n<p>Nếu các bạn cần hỗ trợ nhiều hơn, hãy gọi cho chúng tôi để được tư vấn tận tình nhất.</p>\r\n<p> </p>',	'2018-05-02 12:42:27',	1,	1,	',1,',	1,	0,	'20',	'1',	NULL,	'nam-linh-chi-do-han-quoc',	NULL,	NULL,	1,	NULL,	114),
(2,	'Rượu sâm ngọc linh',	'Rượu sâm ngọc linh giúp bồi bổ sức khỏe giá tốt nhất thị trường. Quà biếu sang trọng, ý nghĩa cho người thân trong các dịp lễ tết.',	'Rượu sâm ngọc linh - Quà biếu sang trọng, ý nghĩa',	'Rượu sâm ngọc linh giúp bồi bổ sức khỏe giá tốt nhất thị trường. Quà biếu sang trọng, ý nghĩa cho người thân trong các dịp lễ tết.',	NULL,	NULL,	'826db-ruou_sam_ngoc_linh.jpg',	'2018-04-12 14:48:12',	'<p><strong>Rượu sâm ngọc linh </strong>chắt lọc những giá trị quý giá từ <a href=\"http://tuthuocnam.com/san-pham/sam-ngoc-linh.html\">Sâm Ngọc Linh</a>. Dòng rượu đẳng cấp, sang trọng, cực kì bổ dưỡng cho sức khỏe. Rượu đã được loại bỏ các độc tố Andehyl, Methanol có hại cho sức khỏe. Sản phẩm được chứa trong chai gốm sứ cao cấp và hộp sơn mài tinh hoa truyền thống của Việt Nam.</p>\r\n<h2>Tác dụng của rượu sâm ngọc linh</h2>\r\n<ul>\r\n<li><strong>Tăng cường chức năng sinh lý</strong>: Đối với những người rối loạn cương dương, xuất tinh sớm, lãnh cảm… nếu sử dụng rượu sâm Ngọc Linh thường xuyên sẽ tăng kích thích hoạt động của tuyến lên não bộ từ đó giúp cải thiện sinh lý ở nam giới.</li>\r\n<li><strong>Chống stress và trầm cảm</strong>: Vì có chất saponin nên rượu sâm Ngọc Linh có tác dụng làm giảm căng thẳng, chống stress và đặc biệt tốt trong việc điều trị bệnh trầm cảm.</li>\r\n<li><strong>Tăng cường hệ miễn dịch</strong>: Rượu sâm Ngọc Linh có khả năng tái tạo tế bào, tăng lượng hồng cầu và phòng chống bệnh thiếu máu. Từ đó giúp choc ơ thể luôn khỏe mạnh và chống lại bệnh tật.</li>\r\n<li><strong>Bảo vệ chức năng gan</strong>: Việc thường xuyên sử dụng rượu sâm Ngọc Linh có tác dụng bảo vệ chức năng gan, giúp cho gan chống lại việc viêm nhiễm hoặc u xơ gan.</li>\r\n</ul>\r\n<h2>Cách ngâm rượu sâm ngọc linh</h2>\r\n<p><strong>Bước 1: Chọn sâm</strong><br /> Sâm để ngâm rượu phải từ 5 năm tuổi trở lên. Sâm có càng nhiều đốt càng tốt. Sâm có chất lượng tốt nhất phải là sâm được tìm thấy ở núi Ngọc Linh có độ cao trên 1000 mét.</p>\r\n<p><strong>Bước 2: Chọn rượu</strong><br /> Chọn rượu 40 độ được kiểm định của Cục vệ sinh và an toàn thực phẩm.</p>\r\n<p><strong>Bước 3: Chọn bình ngâm rượu</strong><br /> Bình để ngâm rượu sâm Ngọc Linh phải là bình thủy tinh. Việc sử dụng bình thủy tinh để ngâm rượu giúp không làm ảnh hưởng đến chất lượng của sâm và có tính thẩm mỹ cao.</p>\r\n<p><strong>Bước 4: Tiến hành ngâm rượu</strong><br /> Khi đã chuẩn bị đủ các vật dụng cần thiết thì chúng ta tiến hành ngâm rượu như sau:<br /> Rửa sạch sâm trước khi ngâm. Việc rửa sâm phải hết sức cẩn thận để vừa đảm bảo việc làm cho củ sâm được sạch vừa không làm gẫy rễ sâm hoặc làm củ sâm bị xây xát. Sau khi rửa sạch sâm Ngọc Linh thì để cho ráo nước. Sau đó cho vào bình và tạo thế cho đẹp. Tiếp theo đổ rượu vào ngập hết phần sâm trong bình rồi đậy nắp bình thật kín giúp rượu không bị bay hơi. Để bình rượu sâm ở nơi thoáng mát và tránh ánh nắng trực tiếp. Rượu Ngọc Linh  ngâm trong thời gian từ 3 tháng trở lên là có thể sử dụng.</p>\r\n<p><strong>Lưu ý</strong></p>\r\n<ul>\r\n<li><strong>Tỷ lệ sâm Ngọc Linh ngâm rượu là cứ 100g sâm Ngọc Linh đem ngâm với 3 lít rượu.</strong></li>\r\n<li>Không được lạm dụng rượu quá nhiều, mà phải sử dụng đúng liều lượng. Mỗi ngày dùng khoảng 50 – 60ml rượu sâm Ngọc Linh là tốt nhất.</li>\r\n</ul>\r\n<h3> </h3>',	'2018-05-01 21:05:10',	1,	1,	',1,',	1,	NULL,	'17',	'1',	0,	'ruou-sam-ngoc-linh',	NULL,	1,	NULL,	NULL,	99),
(3,	'Nấm chaga',	'Nấm Chaga nga - thần dược từ thiên nhiên. Hãy để tủ thuốc nam hỗ trợ chăm sóc cho sức khỏe của cả gia đình bạn ngay từ bây giờ.',	'Nấm chaga nhập khẩu từ Nga trị tiểu đường, ngừa ung thư.',	'Nấm Chaga nga - thần dược từ thiên nhiên, nguồn gốc từ Nga, Siberia. Nó có giá trị cao về dưỡng chất, tác dụng \"thần kỳ\" đối với sức khỏe con người.',	1200,	NULL,	'51756-nam-chaga-nga.jpg',	'2018-04-18 09:48:30',	'<blockquote>\r\n<p>Nấm Chaga - quà tặng từ thiên nhiên. Nấm chaga là loài nấm sống kí sinh trên cây bạch dương, có giá trị cao về dưỡng chất.</p>\r\n</blockquote>\r\n<p>Nấm chaga có nguồn gốc từ Nga Siberia. Người dân nơi đây còn gọi nó là \"món quà của chúa trời\" , \"nấm của sự bất tử \". Người nhật gọi là \"kim cương của rừng xanh\", người Trung Quốc gọi là \"Vua Thực Vật\" hay \"Linh Chi Hoàng Đế\". Những cái tên này nói nên sự quý hiếm và tác dụng \"thần kỳ\" mà nấm chaga mang lại cho sức khỏe con người.</p>\r\n<ul>\r\n<li><em><strong>Xuất xứ</strong></em> : Nấm Chaga có xuất xứ ở Nga.</li>\r\n<li><strong>Giá Nấm Chaga:</strong></li>\r\n</ul>\r\n<p><strong>- Loại 1</strong> : <strong>1.100.000đ/kg</strong> ( Là loại Nấm to, mỗi cục nấm nặng từ 300-400g / cục nấm ) có cục 600 gram. Nấm tùy theo mùa<br /> <strong>- Mua trên 2kg sẽ được giá sỉ:</strong><br /> ► 2kg - 4kg : <strong>Giá 900.000đ/kg</strong><br /> ► 5kg - 10kg : <strong>Giá 700.000đ/kg</strong></p>\r\n<ul>\r\n<li><em><strong>Tác dụng</strong></em> :</li>\r\n</ul>\r\n<p>- Nấm chaga có tác dụng ngăn ngừa bệnh ung thư, tiểu đường.<br /> - Dùng cho người có hệ miễn dịch suy giảm ,người già yếu , cơ thể suy nhược ...<br /> - Cải thiện và phòng ngừa dị ứng vỏ não.<br /> - Nấm Chaga tinh chế ra Alpha-Fucoglucomannan có tác dụng: tăng hệ miễn dịch, hỗ trợ chức năng của não.<br /> - Không tác dụng phụ.<br /> - Ngoài ra nấm Chaga giúp hỗ trợ chống lại các căn bệnh như cảm cúm, đậu mùa. Không những thế còn có thể lọc rửa máu, dành cho người cao huyết áp và giảm viêm nhiễm.<br /> - Hỗ trợ điều trị bệnh rối loạn về da , vẩy nến , eczema.<br /> - Nấm Chaga có thành phần Melanin là chất chống oxy hóa cao giúp chống lão hóa, nâng cao tuổi thọ.</p>\r\n<ul>\r\n<li><em><strong>Cách dùng</strong> </em>:<br /> - <strong>Cách 1</strong>: Cho 9 lát nấm Chaga (sắt lát 1-2mm) với 2 lít nước đun bằng ấm sắc thuốc, sau 2 giờ chắt lấy hết nước lần 1. Tiếp tục cho 2 lít nước vào đun sau 1h30’ chắt hết lấy nước lần 2. Lặp lại như thế ta lấy nước lần 3, lần 4 và lần 5 thì bỏ bã nấm.<br /> Tất cả lượng nước thu được sau 5 lần ta sử dụng dần, lượng chưa sử dụng để ngăn mát tủ lạnh để bảo quản và uống thay nước hàng ngày. Có thể pha thêm nước sôi để pha loãng và làm ấm nước trước khi uống. Có thể uống lần nào thì đun lần ấy, cất bã để lần sau cho nước đun tiếp. Một lượng Chaga như vậy có thể sắc được 5 lần mới bỏ.<br /> - <strong>Cách 2</strong>: Nghiền nhỏ nấm Chaga thành bột. Mỗi lần uống đem hãm 1 muỗng nấu ăn bột nấm Chaga với một lít nước sôi trong bình giữ nhiệt. Sau 30 phút nước có màu cà phê là có thể uống. Uống 3-6 lần mỗi ngày, mỗi lần 100-200 ml. Một ngày cần dùng 600 ml trở lên. Nên uống vào khoảng thời gian giữa các bữa ăn hoặc sau khi ăn 2 tiếng.</li>\r\n<li><em><em><strong>Cách phân biệt thật - giả : </strong></em></em><strong>Nấm chaga Nga</strong> có ngoại hình khá đặc biệt. Bên ngoài có màu đen, cứng, sần sùi giống như vỏ cây cháy xém. Bên trong có màu nâu gỗ, giống với thân cây.<br /> <strong>Nấm Chaga tự nhiên</strong> có giá trị cao hơn <strong>nấm nuôi trồng</strong> cả về giá trị dinh dưỡng cũng như giá trị kinh tế. Nên người dùng cũng cần lưu ý để bảo vệ quyền lợi của mình.<br /> <strong><em>+ Phân biệt dựa trên nhãn mác:</em></strong> Nếu trên nhãn điền chiết xuất từ sợi nấm hoặc sợi nấm nuôi trồng là có thể phân biệt được.<br /> <strong><em>+ Dựa vào đặc điểm nhận dạng:</em> </strong>Nếu như mua nguyên khối nấm, người dùng nên lưu ý.<strong> Nấm tự nhiên </strong>có phần vỏ màu đen và thịt nấm sẽ dày, cứng, có màu đậm. Còn n<strong>ấm nuôi trồng</strong> thì phần thịt nấm sẽ mềm hơn, xốp hơn và màu nhạt.<br /> <em><strong>Đặc biệt khi mua cần lưu ý, phần gốc nấm không có nhiều giá trị, nên tránh mua phần này.</strong></em></li>\r\n</ul>\r\n<h3>Sử dụng sản phẩm của chúng tôi, các bạn có thể hoàn toàn yên tâm. Sản phẩm của chúng tôi đã được cơ quan có thẩm quyền cấp <em><strong>giấy  chứng nhận \" An toàn thực phẩm\". </strong></em></h3>\r\n<p><em><strong>Nấm Chaga</strong></em> của tuthuocnam.com được nhập khẩu từ Nga, không qua trung gian. Chúng tôi đảm bảo cả về chất lượng cũng như về giá thành. Nếu quý khách phát hiện hàng kém chất lượng, chúng tôi sẽ hoàn lại 100% giá trị sản phẩm.</p>\r\n<h2>Video Hướng dẫn pha trà từ nấm chaga</h2>\r\n<p><iframe src=\"https://www.youtube.com/embed/Z84hT0W6YN8\" width=\"100%\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\r\n<p> </p>',	'2018-05-01 21:14:56',	1,	1,	',1,',	1,	NULL,	'23',	'1',	NULL,	'nam-chaga',	NULL,	1,	NULL,	NULL,	35),
(4,	'Sâm ngọc linh',	'Sâm ngọc linh, nhân sâm tốt nhất thế giới có rất nhiều tác dụng đối với sức khỏe. Nó chính là thần dược tốt nhất cho sức khỏe gia đình bạn.',	'Sâm ngọc linh tự nhiên - nhân sâm tốt nhất thế giới.',	'Nhất đẳng hùng sâm - hay chính là sâm ngọc linh. Loại sâm được coi là thần dược. Nó được đánh giá là một trong những loại thuốc bổ đứng đầu tại Việt Nam.',	NULL,	NULL,	'254a0-sam-ngoc-linh.jpg',	'2018-04-18 09:49:50',	'<blockquote>\r\n<p><strong>Sâm ngọc linh</strong> hay còn gọi là \" Nhất  đẳng hùng sâm\". Nó được đánh giá là một trong những loại thuốc bổ hàng đầu. Có tác dụng vô cùng to lớn đối với sức khỏe của con người.</p>\r\n</blockquote>\r\n<h3>Tác dụng sâm ngọc linh mang lại:</h3>\r\n<ul>\r\n<li>Giúp tăng cường thể lực, chống suy nhược cơ thể.</li>\r\n<li>Kích thích não bộ, tăng cường trí nhớ.</li>\r\n<li>Làm giảm căng thẳng, mệt mỏi, chống suy nhược thần kinh.</li>\r\n<li>Tăng cường chức năng sinh lý.</li>\r\n<li>Phòng ngừa bệnh tiểu đường.</li>\r\n<li>Chống lão hóa.</li>\r\n<li>Bổ máu, ngừa bệnh thiếu máu, suy tiểu cầu.</li>\r\n<li>Tăng cường và bảo vệ gan.</li>\r\n<li>Tăng cường chức năng tim, sơ vữa động mạch.</li>\r\n<li>Phòng ngừa bệnh ung thư.</li>\r\n<li>Cải thiện tiêu hóa, giúp hấp thu chất dinh dưỡng.</li>\r\n</ul>\r\n<h3>Cách nhận biết sâm ngọc linh thật - giả:</h3>\r\n<p>Chính vì giá trị kinh tế của loại thần dược này, nên nhiều người đã tìm cách trà trộn, làm giả nó. Để tránh \" tiền mất, tật mang\" chúng tôi mách bạn cách phần biệt sâm ngọc linh.</p>\r\n<p><em><strong>Sâm thật: </strong></em>có nhiều mắt, các mắt lõm vào thân, xếp so le nhau. Cắt lát mỏng thì phần củ có màu vàng nhạt, phần thân hơi tím hoặc xám nhạt. Khi nếm có vị đắng, dư vị về sau thì ngọt.</p>\r\n<p>Sâm thật có mùi thơm nồng đặc trưng, vỏ sâm mỏng và nhẵn. Khi rửa sạch có màu nâu vàng hoặc xanh xám.</p>\r\n<p><em><strong>Sâm giả</strong></em>:  thường làm từ tam thất hoang hoặc củ ráy.</p>\r\n<p>Sâm giả có vỏ dày, sần sùi, màu da tê giác.</p>\r\n<p>Tam thất hoang có hình dạng loằng ngoằng và dài hơn sâm ngọc linh. Trên thân tam thất có chứa nhiều mắt, không có củ chính ( nếu có thì củ nhỏ) và hiếm có rễ con bám quanh. Tam thất có vị đắng, cứng và nhiều sơ hơn sâm ngọc linh thật.</p>\r\n<p><strong><em>Bên cạnh đó sâm ngọc linh trồng chuyên canh và sâm ngọc linh tự nhiên cũng có những đặc điểm riêng biệt như sau:</em></strong></p>\r\n<p><em><strong>Sâm ngọc linh trồng</strong></em> có kích thước khá đồng đều, xung quanh thân có nốt sẹo. Nhiều rễ, ít mắt hơn <em><strong>sâm tự nhiên</strong></em>. Và phần củ nhỏ hơn so với<em><strong> sâm tự nhiên</strong></em>.</p>\r\n<p>Trên đây là những dấu hiệu phân biệt sâm ngọc linh. Hy vọng giúp ích cho các bạn trong việc lựa chọn sản phẩm chất lượng nhất.</p>\r\n<p>Nếu các bạn quan tâm tới sản phẩm hãy ủng hộ chúng tôi. Ở bài viết sau chúng tôi sẽ giới thiệu tới các bạn những cách sử dụng sâm ngọc linh và đối tượng sử dụng.</p>\r\n<p>Chúng tôi luôn sẵn sàng hỗ trợ bạn một cách tốt nhất!</p>\r\n<p>Hãy liên hệ ngay với chúng tôi!</p>',	'2018-05-01 21:11:16',	1,	1,	',1,',	1,	1,	'17',	NULL,	NULL,	'sam-ngoc-linh',	NULL,	0,	NULL,	NULL,	123),
(5,	'Mật ong rừng',	'Mật ong rừng - một tặng phẩm tuyệt vời mà thiên nhiên ban tặng. Từ rất lâu mật ong đã là một dược phẩm không thể thiếu trong mỗi gia đình.',	'Mật ong rừng nguyên chất, uy tín, chất lượng cao!',	'Mật ong rừng nguyên chất - sản phẩm bổ dưỡng thuần khiết đến từ thiên nhiên. Với rất nhiều tác dụng, mật ong từ lâu đã là một dược phẩm không thể thiếu trong mỗi gia đình.',	800,	600,	'ac662-mat-ong-rung.jpg',	'2018-05-01 17:57:01',	'<blockquote>\r\n<p>Mật ong rừng nguyên chất - sản phẩm bổ dưỡng thuần khiết đến từ thiên nhiên.</p>\r\n</blockquote>\r\n<p>Hiện nay mật ong được sử dụng khá phổ biến. Nhưng không hẳn mọi người đều đã hiểu vì sao mật ong tốt? Và cách sử dụng mật ong hiệu quả là gì? Hãy cùng tuthuocnam.com tìm hiểu thêm nhé!</p>\r\n<p>Trong mật ong rừng tự nhiên có tới 75% là hai loại đường  fructose và glucose. 25% còn lại là các khoáng chất Fe, Ca, P, Mg, Cu, Zn... và các loại vitamin B1, B2, B3, B5. Mật ong chứa rất nhiều kháng khuẩn có tính diệt trùng cao.</p>\r\n<h3>Từ những thành phần của mật ong, mà nó có những <em>công dụng</em> riêng biệt:</h3>\r\n<ul>\r\n<li>Mật ong rừng có tính tiệt trùng cao, nên có công dụng rất tốt trong việc điều trị các bệnh ngoài da.</li>\r\n<li>Mật ong rừng có thành phần dinh dưỡng cao nên nó giúp giảm mệt mỏi, cung cấp năng lượng cho cơ thể trong thời gian ngắn.</li>\r\n<li>Hỗ trợ các bệnh về đường hô hấp: trị ho, đau họng.</li>\r\n<li>Chống oxy hóa và các tia cực tím.</li>\r\n<li>Hỗ trọ các bệnh về tiêu hóa, đặc biệt là bệnh dạ dày.</li>\r\n<li>Làm ổn định huyết áp, giảm mỡ trong máu.</li>\r\n<li>Mật ong cũng là một loại mỹ phẩm làm đẹp hữu hiệu. Nó có tác dụng trong việc dưỡng da và chăm sóc tóc.</li>\r\n</ul>\r\n<h3>Lưu ý khi sử dụng mật ong rừng nguyên chất:</h3>\r\n<ul>\r\n<li>Không sử dụng mật ong cho trẻ dưới 12 tháng tuổi. Vì trong mật ong có chưa rất nhiều vi khuẩn có thể sản sinh ra độc tố. Với hệ tiêu hóa chưa phát triển của trẻ không thể ngăn sự phát triển của vi khuẩn có hại. Rất dễ gây ngộ độc hoặc di ứng cho trẻ.</li>\r\n<li>Không pha mật ong với nước quá nóng ( trên 70 độ), như thế sẽ phá hủy cấu trúc enzym và vitamin có lợi cho sức khỏe.</li>\r\n<li>Không bảo quản mật ong trong các chai lọ kim loại, vì nó sẽ gây ra phản ứng hóa học, làm ảnh hưởng tới chất lượng mật ong.</li>\r\n</ul>\r\n<h3>Một số cách sử dụng mật ong hiệu quả:</h3>\r\n<ol>\r\n<li>Một ly nước ấm + 1 muỗng cà phê mật ong + 5-7 giọt nước giấm táo : Uống hỗn hợp này thường xuyên sẽ làm ổn định huyết áp, giảm mỡ máu.</li>\r\n<li>Một muỗng cà phê mật ong + 1 muỗng bột nghệ : Bôi trực tiếp lên vùng lở loét, giúp giả viêm nhiễm.</li>\r\n<li>Một muỗng cà phê mật ong+ 1/2 muỗng bột nghệ : Sử dụng hỗn hợp 2-3 lần/ngày, giúp giảm triệu chứng đau dạ dày.</li>\r\n</ol>\r\n<p>Trên đây là một số chia sẻ của tuthuocnam.com. Chúc các bạn sử dụng mật ong rừng nguyên chất một cách hiệu quả và đúng cách.</p>\r\n<p>Tuthuocnam.com luôn cung cấp các sản phẩm chất lượng, uy tín. Với tiêu chí \" sự hài lòng của khách hàng là vinh dự của chúng tôi\".</p>\r\n<p>Quý khách quan tâm tới sản phẩm hay có bất cứ thắc mắc gì hãy liên hệ ngay với chúng tôi.</p>',	'2018-05-02 12:42:40',	1,	1,	',1,',	1,	NULL,	'25',	NULL,	NULL,	'mat-ong-rung',	NULL,	NULL,	1,	NULL,	7),
(6,	'Nấm lim xanh Quảng Nam',	'Nấm lim xanh Quảng Nam - một trong những loại nấm đứng đầu bảng dược liệu quý trong việc hỗ trợ chữa bệnh ung thư và bổi bổ sức khỏe.',	'Nấm lim xanh Quảng Nam - Chữa bệnh ung thư hiệu quả',	'Nấm lim xanh Quảng Nam là một loại nấm linh chi, được trồng chủ yếu ở vùng núi cao Tiên Phước, tỉnh Quảng Nam. Nó là một trong những loại nấm đứng đầu bảng dược liệu quý.',	NULL,	NULL,	'4cd78-nam-lim-xanh.jpg',	'2018-05-01 21:23:25',	'<p><strong>Nấm lim xanh  Quảng Nam</strong> hay còn được gọi là nấm linh chi Việt Nam. Nó đã được chứng minh là một loại thảo dược quý cho sức khỏe của con người. Nấm lim xanh sinh trưởng và phát triển trên thân hoặc gốc cây lim xanh đã mục. Loại nấm này được trồng chủ yếu ở vùng núi cao Tiên Phước tỉnh Quảng Nam.</p>\r\n<p>Vì lim xanh cũng là một loại nấm linh chi, nên hai loại tương đối giống nhau về hình dáng. Tuy nhiên, do sinh trưởng trên cơ chất khác nhau nên hàm lượng dược tính có sự khác biệt. Chính điều đó tạo nên thế mạnh riêng cho nấm lim xanh.</p>\r\n<h3>Tác dụng của nấm lim xanh:</h3>\r\n<ul>\r\n<li>Phòng chống và hỗ trợ điều trị ung thư rất tốt.</li>\r\n<li>Tăng cường hệ miễn dịch, nâng cao sức đề kháng.</li>\r\n<li>Chống lão hóa.</li>\r\n<li>Giúp giải độc gan, phòng chống và hỗ trợ chữa các bệnh về gan như: gan nhiễm mỡ, xơ gan.</li>\r\n<li>Hỗ trợ chưa bệnh Gout, các bệnh về khớp, cao huyết áp.</li>\r\n<li>Hỗ trợ điều trị các bệnh về tai biến, tiểu đường.</li>\r\n</ul>\r\n<p><em>Chưa có phân tích hóa học rạch ròi nào của các nhà khoa học về sự khác giống và khác nhau về dược liệu trong nấm lim xanh và nấm linh chi. Tuy nhiên, hiệu quả đặc biệt của nấm lim xanh là chữa bệnh ung thư thì đã rõ ràng.</em></p>\r\n<h3>Các cách sử dụng nấm lim xanh hiệu quả:</h3>\r\n<p>Nấm lim xanh hay tên gọi đầy đủ là nấm linh chi lim xanh. Chính vì nấm lim xanh là một loại nấm linh chi nên các cách sử dụng nấm lim xanh hiệu quả cũng giống như các cách mà chúng tôi đã giới thiệu với các bạn sử dụng nấm linh chi hiệu quả.</p>\r\n<p>Các bạn tham khảo link sau nhé: <a title=\"nấm linh chi đỏ Hàn Quốc\" href=\"http://school.com/san-pham/nam-linh-chi-do-han-quoc.html\">cách sử dụng nấm linh chi hiệu quả.</a></p>\r\n<h3> Phân biệt nấm lim xanh và nấm linh chi tự nhiên:</h3>\r\n<p><strong>Nấm lim xanh tự nhiên</strong>: có màu đỏ thẫm, mọc 1 nhánh (nếu mọc 2 nhánh thì mọc từ gốc).</p>\r\n<p>Nấm lim xanh có vị đắng hơn rất nhiều so với nấm linh chi.</p>\r\n<p><strong>Nấm linh chi tự nhiên</strong>: màu đỏ tươi, mọc nhiều nhánh (lên gần tới ngọn mới phân nhánh).</p>\r\n<p>Khối lượng ước lượng trên 1 cây nấm thì nấm lim xanh có khối lượng năng hơn.</p>\r\n<h2>Giá bán nấm lim xanh Quảng Nam:</h2>\r\n<p>Nấm lim xanh Quảng Nam được chúng tôi phân thành 2 loại theo chất lượng, tương ứng với mức giá khác nhau:</p>\r\n<p><strong>Loại 1</strong>: Là loại mà tai nấm có đường kính 3-5cm, thân nấm trung bình, chắc chắn.</p>\r\n<p><strong>Giá nấm lim xanh loại 1: 2.800.000đ/kg</strong>.</p>\r\n<p><strong>Loại 2</strong>: Là loại nấm lim xanh cao cấp, có tuổi thọ lớn hơn loại 1, nên kích thước đường kính tai nấm &gt;6cm, thân nấm cao to, chắc chắn.</p>\r\n<p><strong>Giá nấm lim xanh cao cấp: 4.500.000đ/kg ( Chúng tôi giảm giá 5% cho bệnh nhân đang điều trị ung thư)</strong></p>\r\n<p>Trên đây là một số thông tin về nấm lim xanh. Ở bài viết sau chúng tôi sẽ chia sẻ rõ hơn về các cách phân biệt nấm lim xanh thật - giả. Hy vọng sẽ giúp ích được cho các bạn.</p>\r\n<p><strong>Quan tâm tới nấm lim xanh Quảng Nam, hãy liên hệ với chúng tôi để được tư vấn một cách tốt nhất.</strong></p>',	'2018-05-02 12:38:55',	1,	1,	',1,',	1,	1,	'21',	NULL,	NULL,	'nam-lim-xanh',	NULL,	NULL,	NULL,	NULL,	24),
(7,	'Sâm Ngọc Linh ngâm mật ong',	'Sâm ngọc linh ngâm mật ong rừng là một loại thần dược cực quý cho sức khỏe của gia đình bạn. Liên hệ ngay với chúng tôi để có giá tốt nhất.',	'Sâm ngọc linh ngâm mật ong rừng uy tín, chất lượng.',	'Sâm ngọc linh ngâm mật ong rừng thần dược bảo vệ sức khỏe cho gia đình bạn. Đắt ngay để hưởng giá tốt nhất thị trường.',	3600,	NULL,	'803ec-sam-ngoc-linh-ngam-mat-ong-rung.jpg',	'2018-05-01 21:38:31',	'Nếu bạn đang sở hữu một bình <strong>sâm ngọc linh ngâm mật ong rừng</strong>. Thì chúng tôi chúc mừng bạn, bạn đang có trong tay thần dược cho sức khỏe gia đình bạn. Sâm ngọc linh là loại thảo dược quý như thế nào? Tác dụng ra làm sao? Chúng tôi đã giới thiệu với các bạn ở bài viết về Sâm ngọc linh nói riêng rồi. Hôm nay chúng ta cùng tìm hiểu về sâm ngọc linh ngâm mật ong.\r\n<h2 style=\"text-align: left;\">Dưới đây là cách làm và tác dụng khi kết hợp giữa <strong>thần dược sâm ngọc linh</strong> với <strong>mật ong rừng</strong>.</h2>\r\n<ul>\r\n<li style=\"text-align: left;\"><strong>Cách chọn và ngâm sâm:</strong> Sâm ngọc linh thái lát, ngâm mật ong rừng là sản phẩm được người nhiều người tiêu dùng ưa chuộng. Sâm được chọn chế biến phải là Sâm Ngọc Linh từ 6 năm tuổi trở lên. Nó được chế biến bằng phương pháp hấp cách thuỷ, sau đó tẩm mật ong rừng nguyên chất.</li>\r\n<li style=\"text-align: left;\"><strong>Tác dụng và đối tượng sử dụng:</strong> Người sử dụng sản phẩm này thường xuyên sẽ được tăng cường sinh lực, giảm cholestoron, chống lão hóa, tăng tuổi thọ...phục hồi sức khỏe nhanh chóng.Sâm lát tẩm mật ong phù hợp cho mọi lứa tuổi, đặc biệt là những người lớn tuổi, sức khỏe suy giảm, suy nhược cơ thể, những người mới bị ốm dậy.\r\n<h4>Bạn có thể sử dụng sâm lát tẩm mật ong làm món quà rất ý nghĩa cho bạn bè, gia đình, người thân, hoặc cấp trên của bạn. Đây không chỉ là món quà sang trọng mà còn có ý nghĩa. Nó thể hiện sự quan tâm của bạn với những người thân yêu.</h4>\r\n</li>\r\n</ul>\r\nChỉ cần một cú click bạn có thể xác nhận những điều tôi nói có thực sự đúng hay sai. Tiêu chí của <strong>Tủ thuốc nam</strong> là đưa tới tay người tiêu dùng những sản phẩm chất lượng, nhằm khẳng định thương hiệu cũng như đem lại lòng tin của khách hàng. Hãy cho chúng tôi cơ hội được thể hiện thiện chí của mình.',	'2018-05-01 21:42:33',	1,	1,	',1,',	1,	NULL,	'17',	NULL,	NULL,	'sam-ngoc-linh-ngam-mat-ong',	NULL,	NULL,	NULL,	NULL,	62),
(8,	'An cung ngưu hoàng hoàn rùa vàng',	'An cung ngưu hoàng hoàn rùa vàng - dược phẩm Đông y nổi tiếng hỗ trợ điều trị đột quỵ, tai biến mạch máu lão, xuất huyết não...',	'An cung ngưu hoàng hoàn rùa vàng thuốc điều trị bệnh tai biến',	'An cung ngưu hoàng hoàn rùa vàng - dược phẩm Đông y nổi tiếng hỗ trợ điều trị đột quỵ, tai biến mạch máu não, xuất huyết não...do tuổi già, chế độ dinh dưỡng, áp lực công việc, cuộc sống gây ra.',	3600,	NULL,	'c6299-an-cung-nguu-hoang-hoan-rua-vang.jpg',	'2018-05-01 21:54:08',	'<p><strong>An cung ngưu hoàng hoàn rùa vàng</strong> được biết đến là một loại dược phẩm quý. Nó đã cứu sống không ít bệnh nhân trong tính trạng nguy cấp liên quan tới đột quỵ, tai biến.</p>\r\n<h2>Thành phần chính của An cung nguu hoàng hoàn rùa vàng</h2>\r\n<ul>\r\n<li>Ngưu hoàng nuôi cấy (Vitro cultural calculus bovis): 166.67 mg.</li>\r\n<li>Bột sừng trâu cô đặc (Pulvis Cornus Bubali Concentratus): 333.33 mg.</li>\r\n<li>Xạ hương (Moschus): 41.67 mg.</li>\r\n<li>Trân châu (Margarita): 83.33 mg.</li>\r\n<li>Chu sa (Cinnabaris): 166.67 mg.</li>\r\n<li>Hùng hoàng (Realgar): 166.67 mg.</li>\r\n<li>Hoàng liên (Rhizoma Coptidis): 166.67 mg.</li>\r\n<li>Hoàng cầm (Radix Scutellariae): 166.67 mg.</li>\r\n<li>Chi tử (Fructus Gardeniae): 166.67 mg.</li>\r\n<li>Uất kim (Radix Curcumae): 166.67 mg.</li>\r\n<li>Băng phiến (Borneolum syntheticum): 41.67 mg.</li>\r\n<li>Tá dược: Mật ong tinh luyện, dầu đậu nành, sáp ong vừa đủ 01 viên hoàn mềm.</li>\r\n</ul>\r\n<h2>Tác dụng của An cung ngưu hoàng hoàn</h2>\r\n<h3>An Cung Rùa Vàng có tác dụng phục hồi liệt cho bệnh nhân đột quỵ não</h3>\r\n<p>- Theo nhóm nghiên cứu của Nguyễn Minh Hiện - Bệnh viện Quân y 103, Nguyễn Minh Hà, Nguyễn Công Thực - Viện Y học Cổ truyền Quân đội cho thấy bệnh nhân đột quỵ nhồi máu não cấp giảm liệt rõ rệt.<br /> - Mức độ liệt theo thang điểm Henry: Nhóm được kết hợp điều trị thuốc An Cung liệt tay hoàn toàn trước điều trị là 53,65%, sau điều trị 12,18%. Liệt chân hoàn toàn trước điều trị: 46,33 %, sau điều trị 7,31%. So sánh kết quả điều trị với nhóm chứng, sự hồi phục rõ rệt hơn (P&lt;0,01).<br /> - Mức độ phục hồi theo thang điểm NIHSS: Điểm trung bình trước điều trị của nhóm được kết hợp thuốc An Cung Ngưu Hoàng Hoàn là 7,54 +_3,62, sau điều trị là 3,2 +_2,58 (P&lt;0,001), so sánh kết quả điều trị với nhóm chứng, sự phục hồi rõ rệt hơn (P&lt;0,05).</p>\r\n<h3>An Cung Ngưu Hoàng Hoàn Rùa Vàng có độc tính thấp nên có thể sự dụng an toàn trong điều trị</h3>\r\n<p>Theo kết quả nghiên cứu của Nguyễn Minh Hà, trên động vật thực nghiệm với liều 7,5gr/kg trọng lượng chuột (liều tối đa chuột có thể uống được), chưa xác định được liều gây chết 50% động vật thí nghiệm.</p>\r\n<h3>An Cung Ngưu Hoàng Hoàn tác dụng trên bệnh nhân xuất huyết não</h3>\r\n<p>An Cung Ngưu Hoàng Hoàn có tác dụng thanh tâm giải độc, thông đàm, bình can tức phong, tỉnh não khai khiếu. Thuốc làm cải thiện tình trạng hôn mê, liệt nửa người, thất ngôn.</p>\r\n<h3>Hãy cùng chúng tôi tìm hiểu rõ hơn về cơ chế tác dụng của an cung ngưu rùa vàng</h3>\r\n<ul>\r\n<li>An cung ngưu hoàng hoàn rùa vàng đi vào cơ thể làm tan cục máu đông.</li>\r\n<li>Kích thích vùng não tổn thương, vùng não đang hấp hối chết.</li>\r\n<li>An thần, giảm lượng tiêu thụ oxy trong não. Giúp não sống lâu nhất trong môi trường thiếu oxy.</li>\r\n<li>Kích thích não bộ, giúp bệnh nhân không bị liệt cơ, khả năng tiểu tiện tự chủ.</li>\r\n<li>Lấy lại sức khỏe cho cơ thể, tác động lên dây thần kinh, giúp bệnh nhân dễ nói hơn, tránh nói ngọng,lắp...</li>\r\n<li>An cung ngưu khi đi vào cơ thể còn giúp bệnh nhân tỉnh táo hơn, tăng lượng máu. Giúp giãn mạch máu bị hẹp, lưu thông máu ổn định hơn.</li>\r\n</ul>\r\n<p><em><strong>An cung ngưu hoàng hoàn rùa vàng dùng điều trị , cấp cứu cho những bệnh nhân trong trường hợp sau:</strong></em></p>\r\n<ul>\r\n<li>Xuất huyết não.</li>\r\n<li>Nhồi máu não.</li>\r\n<li>Viêm não.</li>\r\n<li>Sốt cao, co giật, rối loạn trung tâm điều nhiệt ở thân não.</li>\r\n<li>Ngoài ra sản phẩm còn giúp bệnh nhân giảm di chứng sau tai biến mạch máu não.</li>\r\n</ul>\r\n<h2>Cách sử dụng</h2>\r\n<p>Dùng đường uống:<br /> - Người lớn: 1 viên/lần, 1 lần/ngày<br /> - Trẻ em dưới 3 tuổi: ¼ viên/lần, 1 lần/ngày<br /> - Từ 4 - 6 tuổi: ½ viên/lần, 1 lần/ngày<br /> Liều dùng để phòng:<br /> - Thông thường 1 tháng/viên.<br /> - Tùy thể trạng của bệnh nhân mà điều chỉnh liều cho phù hợp.<br /> - Bệnh nhân khi có nhu cầu điều trị bằng an cung ngưu hoàng hoàn chúng tôi sẽ tư vấn trực tiếp về liều dùng và cách sử dụng hợp lý.</p>\r\n<h2>Chống chỉ định</h2>\r\n<ul>\r\n<li>Người bị đột quỵ, tai biến mạch máu não có hạ thân nhiệt, tụt huyết áp, xuất huyết toàn bộ não thì không nên dùng An Cung Rùa Vàng.</li>\r\n<li>Không dùng cho phụ nữ có thai và cho con bú.</li>\r\n<li>Thận trọng với người suy thận.</li>\r\n<li>Không dùng quá liều chỉ định.</li>\r\n</ul>\r\n<h3>Hiện tại chúng tôi có bán 2 loại An cung ngưu hoàng hoàn rùa vàng</h3>\r\n<ul>\r\n<li>An cung ngưu loại 1: Hộp to chứa 3 hộp nhỏ x 1 viên hoàn mềm 3g</li>\r\n</ul>\r\n<p>Giá bán : 3.600.000đ</p>\r\n<ul>\r\n<li><a href=\"http://tuthuocnam.com/san-pham/cung-nguu-hoang-hoan-hieu-rua-vang.html\">An cung ngưu loại 2</a>: Hộp nhỏ chứa 1 hộp nhỏ x 1 viên hoàn mềm 3g</li>\r\n</ul>\r\n<p>Giá bán: 1.400.000đ</p>\r\n<p><strong><em>Tuthuocnam.com chuyên cung cấp các sản phẩm uy tín, chất lượng.</em></strong></p>\r\n<ol>\r\n<li>Chúng tôi cam kết sản phẩm của chúng tôi uy tín, chất lượng. Được cấp giấy phép lưu hành toàn quốc.</li>\r\n<li>Quan tâm tới sản phẩm hãy liên lạc với chúng tôi để được hướng dẫn cụ thể và sử dụng một cách hiệu quả nhất.</li>\r\n<li>Khi mua sản phẩm các bạn sẽ được giao tận nơi, mua với số lượng lớn quý khách hàng sẽ được giảm giá đặc biệt.</li>\r\n</ol>',	NULL,	1,	NULL,	NULL,	NULL,	NULL,	'23,24',	NULL,	NULL,	'an-cung-nguu-rua-vang',	NULL,	NULL,	NULL,	NULL,	24),
(9,	'Nhân sâm tươi hàn quốc loại 8 củ/1kg',	'Nhân sâm tươi Hàn Quốc loại 8 củ/1kg là loại sâm tươi Hàn Quốc được nhiều người sử dụng nhất, giúp bồi bổ sức khỏe, làm quà biếu sếp...',	'Nhân sâm tươi Hàn quốc loại 8 củ/1kg uy tín, giá rẻ nhất.',	'<strong>Sâm tươi Hàn Quốc</strong> 6 năm tuổi là sâm Hàn quốc đang được nhiều người sử dụng nhất để bổi bổ sức khỏe, làm quà biếu ông bà, cha mẹ, sếp, bạn bè... trong các ngày đặc biệt.',	2500,	NULL,	'aff4c-sam-tuoi-han-quoc-loai-8-cu-1kg.jpg',	'2018-05-01 22:00:12',	'<blockquote>\r\n<p><strong>Nhân sâm tươi Hàn Quốc</strong> loại 8 củ/1kg đều là nhân sâm 6 năm tuổi với kích thước to bé khác nhau. Đây là loại sâm tươi Hàn Quốc được nhiều người sử dụng nhất. Chúng có tác dụng bồi bổ toàn diện cho cơ thể, bổ máu, giúp cơ thể nhanh chóng phục hồi sức khỏe. Là \"thượng phẩn dinh dưỡng\" của đông y.</p>\r\n</blockquote>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 8 củ/1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Cải thiện làn da, giúp da trở nên trẻ trung và mịn màng hơn.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<ul>\r\n<li>Nhân sâm tươi Hàn quốc 6 năm tuổi thích hợp với mọi đối tượng. Mọi người sử dụng sâm với mục đích bổi bổ cơ thể, phòng chống stress làm cơ thể suy yếu.</li>\r\n<li>Người làm việc nhiều, tinh thần mệt mỏi nên dùng sâm tươi Hàn quốc thường xuyên giúp duy trì trạng thái tỉnh táo và chống mệt mỏi.</li>\r\n<li>Người bị mắc bệnh ung thư thì không nên sử dụng nhân sâm Hàn Quốc.</li>\r\n</ul>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>\r\n<h2>Cách nhận biết Nhân sâm Hàn Quốc thật</h2>\r\n<ul>\r\n<li><strong>Nhân sâm Hàn Quốc</strong> có hình dáng giống người.</li>\r\n<li>Sâm vẫn còn đất bám xung quanh.</li>\r\n<li>Phần trên đầu củ sâm có dấu vết tối thiểu của hai lần rụng hoa.</li>\r\n<li>Phần thân nhìn rất rắn chắc.</li>\r\n<li>Có nhiều rễ nhỏ chỉ bám vào chân sâm chứ không bám nhiều vào thân củ.</li>\r\n<li>Mùi của nhân sâm Hàn Quốc rất thơm.</li>\r\n<li>Ứng với mỗi vùng đất khác nhau , củ sâm sẽ có màu khác nhau.</li>\r\n</ul>\r\n<h2>Nhân sâm Hàn Quốc giả</h2>\r\n<ul>\r\n<li>Một số người bán đã dùng đậu đũa dại, thương lục, niễng rừng, hoa sơn sâm, trong đó sâm đất hay được dùng nhất để làm giả nhân sâm Hàn Quốc.</li>\r\n<li>Sâm đất có hình nón hoặc hình thoi. Khi mới đào lên, bề mặt có màu đen nâu, thô ráp, nhiều vằn. Khi đã sơ chế, bề mặt có vằn rúm màu vàng nâu, thô ráp, rất giòn, dễ bẻ gãy, có chất keo trong mờ, vị ngọt.</li>\r\n<li>Thương lục có hình trụ, khi cắt ra có màu nâu vàng đến nâu đen, không phẳng, có mùi tanh, vị đắng và cay chua.</li>\r\n</ul>\r\n<div><img class=\"size-full wp-image-5685 aligncenter\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Phân biệt thật giả sâm Hàn Quốc\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/phan-biet-sam-han-quoc-that-gia.jpg\" alt=\"Phân biệt thật giả sâm Hàn Quốc\" width=\"320\" height=\"203\" /></div>\r\n<div style=\"text-align: center;\"><em>Ảnh phân biệt sâm Hàn Quốc thật giả.</em></div>\r\n<h2>Chứng nhận tiêu chuẩn sản phẩm</h2>\r\n<p><img class=\"alignnone size-full wp-image-5684\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Giấy chứng nhận tiêu chuẩn sản phẩm\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/giay-chung-nhan-tieu-chuan-san-pham.jpg\" alt=\"Giấy chứng nhận tiêu chuẩn sản phẩm\" width=\"650\" height=\"916\" /></p>',	'2018-05-05 15:17:04',	1,	1,	',1,',	NULL,	NULL,	'18,19',	NULL,	NULL,	'nhan-sam-tuoi-han-quoc-loai-8-cu-1kg',	NULL,	NULL,	NULL,	NULL,	27),
(10,	'Đông trùng hạ thảo Hàn Quốc hộp gỗ 30 viên cao cấp',	'Đông trùng hạ thảo Hàn Quốc hộp gỗ 30 viên cao cấp giúp phòng chống ung thư, làm chậm quá trình lão hóa, tăng cường sinh lực...',	'Đông trùng hạ thảo Hàn Quốc hộp gỗ 30 viên cao cấp.',	'Đông trùng hạ thảo Hàn Quốc hộp gỗ 30 viên cao cấp, bổ thận, bổ phế giúp phục hồi sức khỏe, chống lại các bệnh tật và tăng cường chức năng sinh lý cho nam giới.',	1900,	NULL,	'4f93b-dong_trung_ha_thao_han_quoc_hop_30_vien_cao_cap.jpg',	'2018-05-01 22:07:51',	'<blockquote>\r\n<p><strong>Đông trùng hạ thảo Hàn Quốc</strong> hộp gỗ 30 viên là viên uống cực tốt cho sức khỏe. Nó có vị ngọt, tính ấm giúp bổ thận, dưỡng phế. Vì vậy đông trùng hạ thảo có tác dụng rất tốt với các chứng bệnh như thận hư, đau lưng, mỏi gối, liệt dương, di tinh, ho hen...</p>\r\n</blockquote>\r\n<div><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/dong_trung_ha_thao_han_quoc.jpg\" alt=\"\" width=\"319\" height=\"185\" /></div>\r\n<div style=\"text-align: center;\"><em>Đông trùng hạ thảo Hàn Quốc</em></div>\r\n<p><strong>Thành phần của đông trùng hạ thảo Hàn Quốc hộp gỗ 30 viên:</strong><br /> Chiết xuất bột đông trùng hạ thảo (78,65%), màu thực phẩm, Titanium Dioxide, Genlatin.</p>\r\n<p><strong>Xuất xứ</strong>: Hàn Quốc<br /> <strong>Nhà sản xuất</strong>: Korea Ginseng Bio- Science Co., LTD<br /> <strong>Hạn sử dụng</strong>: 36 tháng kể từ ngày sản xuất. Lô sản xuất, hạn dùng được in trên nhãn bao bì.</p>\r\n<h2>Tác dụng của đông trùng hạ thảo Hàn Quốc</h2>\r\n<ul>\r\n<li><strong>Bảo vệ thận</strong>: Viên Đông trùng hạ thảo có tác dụng chống lại các tác dụng xấu của tân dược đối với thận, ví dụ đối với độc tính của Cephalosporin A.</li>\r\n<li>Bảo vệ khí quản, trừ đờm.</li>\r\n<li>Làm giảm lượng Cholesterol giúp lưu thông máu, chống lại căn bệnh xơ vữa động mạch.</li>\r\n<li><strong>Phòng chống ung thư</strong>: Tăng cường tính miễn dịch không đặc hiệu, cũng như năng lực thực bào của các tế bào miễn dịch. Nâng cao khả năng chống ung thư của cơ thể.</li>\r\n<li>Tăng cường chức năng sinh lý ở độ trên 35 tuổi. Hỗ trợ tích cực trong <strong>điều trị liệt dương, c</strong>ó tác dụng tích cực đối với bệnh yếu sinh lý.</li>\r\n<li>Làm chậm quá trình lão hóa, giúp khéo dài tuổi thọ.</li>\r\n<li>Giúp cơ thể hấp thụ tốt các chất dinh dưỡng, tăng cường chức năng tiêu hóa.</li>\r\n<li>Giúp điều hòa huyết áp, làm hạ huyết áp ở người cao huyết áp, giữ ổn định nhịp tim, tăng cường tác dụng lưu thông máu trong cơ thể.</li>\r\n<li>Bồi bổ sức khỏe chống mệt mỏi, tăng cường sinh lực, hệ miễn dịch để cơ thể phòng chống lại bệnh tật.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng</h2>\r\n<ul>\r\n<li>Người bị suy nhược cơ thể, mệt mỏi, căng thẳng tinh thần.</li>\r\n<li>Người cao tuổi.</li>\r\n<li>Người đang phục hồi sức khỏe.</li>\r\n<li>Người kém tập trung trong học tập.</li>\r\n</ul>\r\n<h2>Hướng dẫn sử dụng và bảo quản</h2>\r\n<p><strong>Cách sử dụng</strong>: Mỗi ngày uống 1 viên, sau ăn. Mỗi đợt dùng 2-3 hộp.<br /> <strong>Bảo quản</strong>: Đậy kín hộp sau khi mở, để nơi khô ráo thoáng mát, tránh ánh nắng trực tiếp của mặt trời.</p>\r\n<p><strong>Lưu ý</strong>: <strong><em>Không sủ dụng đông trùng hạ thảo Hàn Quốc cho trẻ em dưới 14 tuổi. Sản phẩm này không phải là thuốc, không có tác dụng thay thế thuốc chữa bệnh.</em></strong></p>',	'2018-05-05 15:16:05',	1,	1,	',1,',	1,	NULL,	'22',	NULL,	NULL,	'dong-trung-ha-thao-han-quoc-hop-go-30-vien-cao-cap',	NULL,	NULL,	NULL,	NULL,	6),
(11,	'Tổ yến sào tinh chế loại 1',	'Tổ yến sào tinh chế loại 1. Tốt cho mọi đối tượng, mọi lứa tuổi, từ người khỏe mạnh đến người bị bệnh, từ người già đến người trẻ.',	'Tổ yến sào tinh chế loại 1, chất lượng, giá rẻ nhất thị trường.',	'<strong>Tổ yến sào tinh chế loại 1 </strong>thượng phẩm thơm ngon, bổ dưỡng món ăn của các bậc vua, chúa quyền quý cực tốt cho sức khỏe.',	2300,	NULL,	'07450-to_yen_sao_tinh_che_loai_1.jpg',	'2018-05-01 22:22:04',	'<p><strong>Tổ yến</strong> là tên một loại thực phẩm - dược phẩm nổi tiếng được làm từ nước bọt của chim yến. Tổ yến có hình dạng như cái bát dính trên vách đá hoặc trong hang động - nơi chim yến sống. Nó bao gồm nhiều phiến mỏng được dệt từ nhiều sợi tơ bằng nước bọt của chim yến và bện vào nhau.</p>\r\n<p><strong>Tổ yến sào tinh chế loại 1 </strong>là tổ yến đã được sơ chế làm sạch lông từ tổ yến thô. Từ xa xưa, tổ yến đã được dùng làm món ăn cho các bậc vua chúa quyền quý. Ngày nay rất nhiều người tìm mua tổ yến. Những người có điều kiện là có thể thưởng thức món ăn thơm ngon bổ dưỡng này.</p>\r\n<div><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/to_yen_sao.jpg\" alt=\"\" width=\"319\" height=\"222\" /><br /><br /></div>\r\n<div style=\"text-align: center;\"><em>Tổ yến sào tinh chế loại 1</em></div>\r\n<p><strong>Có 3 loại tổ yến được bán trên thị trường</strong>: Tổ yến thô, tổ yến tinh chế, tổ yến sơ chế. Tổ yến tinh chế có thể sử dụng được ngay, rất tiện lợi cho người sử dụng.</p>\r\n<h2>Tác dụng của tổ yến sào tinh chế loại 1</h2>\r\n<ul>\r\n<li>Hỗ trợ phòng chống ung thư nhờ có nhiều chất chống oxy hóa.</li>\r\n<li>Điều trị các bệnh ung thư và HIV/AIDS vì phát hiện có một số hoạt chất sinh học kích thích sinh trưởng tế bào bạch cầu ngoại biên.</li>\r\n<li>Giúp giảm căng thẳng, mệt mỏi cho những người lao tâm, lao lực.</li>\r\n<li>Chống lão hóa và giúp da luôn giữ được nét trẻ trung.</li>\r\n<li>Giúp giảm ho, tiêu đàm. Tốt cho những người bị hen suyễn, ho dai dẳng, ho ra đàm, và viêm phế quản.</li>\r\n<li>Cải thiện chức năng tim, điều hòa lưu thông máu đi khắp cơ thể.</li>\r\n<li>Hỗ trợ tái tạo và tăng trưởng của tế bào.</li>\r\n<li>Tăng cường trí nhớ, giúp tinh thần tỉnh táo.</li>\r\n</ul>\r\n<h2>Cách phân biệt Yến sào thật và giả</h2>\r\n<p><strong>Cách 1: Ngâm tổ yến vào nước.</strong><br /> + Tổ Yến giả: gặp nước sẽ nở ngay sau 2-3 phút, sau đó sẽ nhão ra.<br /> + Tổ Yến thật: khi ngâm nước sẽ không bị tan và nhão ra.</p>\r\n<p><strong>Cách 2: Đun sôi tổ yến.</strong><br /> + Tổ Yến giả: khi đun có mùi carbonat natri rất hắc, yến bị tan ra. Khi sôi có nhiều bọt, để qua đêm có màu vàng, hôi như bị thiu.<br /> + Tổ Yến thật: khi đun sôi không bị tan ra mà chỉ thành những sợi Yến nguyên vẹn, ít bọt, có mùi đặc trưng của Yến.</p>\r\n<p><strong>Cách 3: Nhỏ thuốc Luigon vào tổ yến khi đun sôi.</strong><br /> + Tổ Yến giả: Nhỏ thuốc vào khi đun sôi có phản ứng tạo màu xanh.<br /> + Tổ Yến thật: Nhỏ thuốc vào khi đun sôi sẽ không có phản ứng tạo màu.</p>\r\n<h2>Cách dùng tổ yến</h2>\r\n<p>Nên dùng trước khi ngủ từ 10-15 phút vì khi ngủ cơ thể sẽ hấp thụ các dưỡng chất có trong yến sào một cách tốt nhất. Nên ăn yến sào với liều lượng vừa phải để tiết kiệm chi phí và đem lại hiệu quả tốt nhất.</p>\r\n<h2>Cách bảo quản tổ yến</h2>\r\n<p>Bảo quản ở nơi khô ráo, thoáng mát, tránh ánh nắng mặt trời, càng tốt khi được bảo quản ở điều kiện lạnh (như trong ngăn mát tủ lạnh).</p>\r\n<p><strong>Hãng sản xuất</strong>: Công ty TNHH Bảo kim Long.</p>\r\n<h2>Cam kết bán hàng</h2>\r\n<ul>\r\n<li>Hàng đủ cân.</li>\r\n<li>Hàng khô hoàn toàn.</li>\r\n<li>Không chất bảo quản, chất độn, phụ gia.</li>\r\n<li>Hoàn lại tiền 200% khi sản phẩm không đúng chất lượng.</li>\r\n</ul>',	'2018-05-05 15:15:40',	1,	1,	',1,',	1,	NULL,	'26',	NULL,	NULL,	'to-yen-sao-tinh-che-loai-1',	NULL,	NULL,	NULL,	NULL,	4),
(12,	'Xáo tam phân khô',	'Xáo tam phân là một loại thảo dược quý hiếm, có tác dụng hỗ trợ điều trị 5 loại bệnh ung thư ác tính, các bệnh cao huyết áp, đường huyết...',	'Xáo tam phân phơi khô thái lát - Điều trị bệnh ung thư.',	'<strong>Xáo tam phân</strong> thần dược hỗ trợ điều trị 5 loại bệnh ung thư ác tính: ung thư gan, ung thư đại tràng, ung thư phổi, ung thư vòm họng, ung thư vú.',	2500,	NULL,	'7405f-xao_tam_phan_kho.jpg',	'2018-05-01 22:40:30',	'<p><strong>Xáo tam phân</strong> là thảo dược quý hiếm, có nguồn gốc từ Ninh Hòa - Khánh Hòa. Nó được biết đến như là thần dược hỗ trợ điều trị 5 loại bệnh ung thư: ung thư gan, ung thư đại tràng, ung thư phổi, ung thư vòm họng, ung thư vú. Ngoài ra xáo tam phân còn có tác dụng rất tốt với các bệnh cao huyết áp, đường huyết, nhức mỏi, rối loạn tiền đình…</p>\r\n<p>Bộ phận dùng để làm thuốc của Xáo tam phân là thân và rễ. Tuy nhiên các dược chất, tinh dầu chủ yếu tập trung ở rễ. Xáo tam phân thật luôn có mùi thơm sữa đặc trưng.</p>\r\n<p><img class=\"w-100\" title=\"Xáo tam phân\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/xao_tam_phan_kho_2.jpg\" alt=\"Xáo tam phân\" width=\"960\" height=\"704\" /></p>\r\n<p style=\"text-align: center;\"><em><img class=\"w-100\" title=\"Xáo tam phân\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/xao_tam_phan.jpg\" alt=\"Xáo tam phân\" width=\"717\" height=\"960\" /><br />Sản phẩm Xáo tam phân khô của chúng tôi.</em></p>\r\n<h2>Cách dùng xáo tam phân</h2>\r\n<ul>\r\n<li><strong>Dùng để ngâm rượu</strong>: Xáo tam phân dùng để ngâm rượu phải chọn cây từ 5 năm tuổi trở nên. Trước khi ngâm rửa sạch lớp đất bán vào rể, rửa nhẹ nhàng tránh làm trầy rễ. Chọn rượu ngâm khoảng 40 - 50 độ.  Ngâm xáo tam phân khoảng 2 tháng là dùng được. Uống mỗi ngày không quá 100ml. Uống thường xuyên và điều độ loại rượu này sẽ giúp cho xương khớp hết nhức mỏi, khí huyết lưu thông và phòng tránh bệnh tật.</li>\r\n<li><strong>Sắc lấy nước uống</strong>: Xáo tam phân cắt lát nhỏ cho vào xoong hoặc chảo sao vàng, hạ thổ để nguội. Sau đó đem đi rửa sơ qua với nước sạch và đem sắc nước uống thay cho nước uống hàng ngày. Uống nước Xáo tam phân đều đặn giúp phòng chống bệnh tật, mang lại tinh thần sảng khoái. Đặc biệt xáo tam phân có khả năng điều hòa kinh nguyệt, nhanh phục hồi sức khỏe cho phụ nữ sau sinh.</li>\r\n</ul>\r\n<h2>Cách phân biệt xáo tam phân thật giả</h2>\r\n<p><strong>Xáo tam phân tự nhiên</strong> có màu vàng nhạt, mùi thơm nhẹ đặt trưng. Do rễ xáo tam phân có chứa nhiều tinh dầu nên khi đốt sẽ có mùi giống mùi sữa.<br /> Khi cắn thử sẽ có vị hơi đắng, the the ở đầu lưỡi và mùi thơm dễ chịu như sâm. Lớp võ bên ngoài nhìn mịn dễ bị trầy xướt khi có ma sát. Do đó thường thì sẽ có một lớp đất mịn, mỏng còn bám lại ở rễ cây mặc dù đã phơi khô. Khi dùng ngâm rượu sẽ có màu vàng nhạt rất đẹp.</p>',	'2018-05-05 15:15:11',	1,	1,	',1,',	1,	NULL,	'27',	NULL,	NULL,	'xao-tam-phan-kho',	NULL,	NULL,	NULL,	NULL,	4),
(13,	'Bình ngâm rượu Hàn Quốc N39 - 3L',	'Bình ngâm rượu Hàn Quốc N39- 3L, sang trọng, đẹp mắt. Sản phẩm vừa để ngâm rượu vừa để trang trí cho gia đình bạn.',	'Bình ngâm rượu Hàn Quốc N39 - 3L, sang trọng, đẹp mắt.',	'Bình ngâm rượu Hàn Quốc N39 - 3 lít sang trọng, đẹp mắt. Sản phẩm vừa để ngâm rượu vừa để trang trí cho gia đình bạn.',	350,	NULL,	'dd183-binh-ngam-ruou-n39.jpg',	'2018-05-01 22:50:41',	'<p><strong>Bình ngâm rượu Hàn Quốc N39 - 3 lít</strong> được làm từ thủy tinh cao cấp đem lại hình ảnh vật ngâm chân thực, trong suốt, vật ngâm được phóng to nhờ thấu kính phân kì từ vỏ bình, cho bình rượu thêm sống động và đẹp mắt. Nắp và đáy bình được làm từ nhựa cao cấp rất bền, được phủ lớp si vàng bóng không bong tróc. Bình được nhập khẩu từ Hàn quốc, nhìn rất sang trọng thích hợp để ngâm thảo dược, nhân sâm tươi Hàn quốc...</p>\r\n<h2>Bình ngâm rượu Hàn Quốc N39 - 3L</h2>\r\n<ul>\r\n<li>Xuất sứ: Hàn Quốc.</li>\r\n<li>Chiều cao bình: 40 cm.</li>\r\n<li>Thể tích: 3 lít.</li>\r\n<li>Đường kính miệng: 8 cm.</li>\r\n<li>Chi vi tối đa bình: 54 cm.</li>\r\n<li>Hình dán: ovan đứng.</li>\r\n<li>Sản phẩm gồm: nắp nhựa, thân thủy tinh, đế nhựa, kèm thùng carton bên ngoài.</li>\r\n</ul>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/binh-n39-3l.jpg\" alt=\"Bình ngâm rượu Hàn Quốc\" width=\"259\" height=\"403\" /></p>\r\n<p style=\"text-align: center;\"><em>Bình N39 - 3L.</em></p>\r\n<h2>Hướng dẫn sử dụng</h2>\r\n<ul>\r\n<li>Mở bình bằng cách xoay nắp phía trên bình. Bên trong nắp có lớp cilicon giữ cho bình kín, rượu không bị bay hơi.</li>\r\n<li>Vệ sinh bình: dùng vài mềm với nước để lau chùi.</li>\r\n<li>Không búng tay vào bình, có thể làm thủy tinh bị nứt vỡ.</li>\r\n<li>Không dùng dụng cụ vể sinh băng kim loại để lau bình.</li>\r\n<li>Bình thích hợp để ngâm sâm loại 5 củ/1kg hoặc 6 củ/1kg.</li>\r\n</ul>',	'2018-05-05 15:14:21',	1,	1,	',1,',	1,	NULL,	'29',	NULL,	NULL,	'binh-ngam-ruou-han-quoc-n39-3l',	NULL,	NULL,	NULL,	NULL,	2),
(14,	'Cao ban long Sibiri',	'Cao ban long Sibiri là loại cao được chiết xuất từ sừng già của loài Hươu Maral và Hươu tuyết sống ở núi Altai Sibiri Nga.',	'Cao ban long Sibiri nhập khẩu từ Nga - Quà biếu cao cấp.',	'<strong>Cao ban long Sibiri</strong> là loại cao được chiết xuất từ sừng già của loài Hươu Maral và Hươu tuyết sống ở núi Altai Sibiri Nga. Nó là một trong những dược phẩm hàng đầu của đông y.',	1200,	NULL,	'd4e4d-cao_ban_long_sibiri.jpg',	'2018-05-01 23:02:57',	'<p><strong>Cao ban long</strong> hay còn có tên gọi khác là bạch giao, lộc giác giao. Cao được chể biến từ sừng già(gạc) của Hươu là dược phẩm quý hàng đầu của đông y. <em>Theo y học cổ truyền phương đông Cao ban long có vị ngọt, mặn, tính ấm, quy vào hai kinh, can và thận, có tác dụng bổ trung, ích khí, cường tinh, hoạt huyết, cầm máu, mạnh gân xương.</em></p>\r\n<p><strong>Cao ban long sibiri</strong> là dạng cao dẻo, màu nâu vàng được chế biến từ gạc của loài Hươu Maral và Hươu tuyết sống ở núi Altai Sibiri Nga. Hươu được sống trong môi trường giáp rừng yên tĩnh, không bị ô nhiễm. Chúng được ăn nhiều loại nấm, cây cỏ - thảo dược khác nhau... nên nhung và gạc đặc biệt tốt.</p>\r\n<div><img class=\"alignnone size-full wp-image-5755\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/cao_ban_long_sibiri_nga.jpg\" alt=\"Cao ban long sibiri Nga\" width=\"700\" height=\"484\" /></div>\r\n<div style=\"text-align: center;\"><em><br />Cao ban long sibiri nga hộp quà biếu sang trọng.</em></div>\r\n<h2>Cách làm Cao ban long</h2>\r\n<p>Cao ban long được chế biến theo y học cổ truyền Trung Hoa. Gạc hươu đùng để làm cao được thu lượm với lượng huyết chưa bị phân hủy.  Sau đó dùng nước đun lá cây để tẩy rửa, phơi 3 sương cho trắng, lấy nước mưa nấu cao, đun ở nhiệt độ quy định, thời gian nấu đủ. Cô cao đúng quy cách nên cao không bị mất chất và các vitamin cần thiết).</p>\r\n<h2>Thành phần của cao ban long</h2>\r\n<ul>\r\n<li>Sừng già (gạc) hươu: là thành phần có chứa 52,50% protid, 2,50% lipid, chất keo (keratin) rất tốt cho sự phát triển cơ thể, cơ bắp, tăng cường sự dẻo dai và tuổi thọ cho người già.</li>\r\n<li>Một lượng nhỏ huyết hươu: có tác dụng tẩm bổ toàn thân, hỗ trợ điều trị suy nhược cơ thể và ngăn ngừa bệnh tật hiệu quả.</li>\r\n<li>Ngoài ra, cao ban long còn có nước sạch tinh chất và long nhãn giúp dễ uống hơn.</li>\r\n</ul>\r\n<h2>Tác dụng của cao ban long sibiri</h2>\r\n<ul>\r\n<li>Giúp bổ trung, ích khí, cường tinh, cầm máu, tiêu sưng, mạnh gân xương, bổ khí huyết, ích tinh tủy và bồi bổ sức khỏe sau khi ốm.</li>\r\n<li>Cung cấp hàm lượng collagen cao giúp hệ xương khớp khỏe mạnh, hạn chế đau nhức cơ khi thời tiết thay đổi, do vận động, đẩy lùi lão hóa sớm cho da, giúp da mịn màng, tươi tắn hơn.</li>\r\n<li>Hỗ trợ phòng chống các căn bệnh mãn tính như ung thư, tim mạch, tai biến mạch máu não…</li>\r\n<li>Tăng cường khả năng đề kháng cho cơ thể, hạn chế mệt mỏi suy nhược.</li>\r\n<li>Bổ thận, nâng cao chức năng sinh lý nam giới và khả năng ham muốn cho các cặp vợ chồng.</li>\r\n<li>Giúp ăn ngon, ngủ tốt, giảm stress do áp lực công việc và cuộc sống.</li>\r\n<li>Hạn chế còi xương, suy dinh dưỡng cho trẻ nhỏ.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng cao ban long</h2>\r\n<ul>\r\n<li>Dùng cho người suy nhược cơ thể, gầy yếu và suy dinh dưỡng.</li>\r\n<li>Người xuất huyết dưới da và niêm mạc, chảy máu cam, mất máu sau phẫu thuật, phụ nữ kinh nguyệt quá nhiều.</li>\r\n<li>Trường hợp thận hư, đau lưng mỏi gối, tiểu nhiều về đêm và ra mồ hôi trộm.</li>\r\n</ul>\r\n<p><strong>Chú ý</strong>: người có bệnh hư hàn thì không nên dùng.</p>\r\n<h2>Cách sử dụng</h2>\r\n<p>Dùng cho cả nam và nữ đặc biệt là từ giai đọan trung niên. Ngày uống 10 – 15g, cắt cao thành miếng mỏng nhai hoặc ngậm rồi nuốt. Có thể ăn cao với cháo nóng, long nhãn hoặc cho vào ít rượu, hâm nóng uống.</p>\r\n<h2>Bảo quản</h2>\r\n<p>Cao ban long sibiri không dùng chất bảo quản, để nơi khô ráo, tránh ánh sáng trực tiếp. Nên để để trong ngăn mát tủ lạnh ở nhiệt độ 15 – 20 độ khi đã mở ra dùng.<br /> Thời hạn sử dụng: 2 năm kể từ ngày sản xuất. Ngày sản xuất và hạn sử dụng in trên vỏ bao bì.</p>',	'2018-05-05 15:13:29',	1,	1,	',1,',	1,	NULL,	'23',	NULL,	NULL,	'cao-ban-long-sibiri',	NULL,	NULL,	NULL,	NULL,	3),
(15,	'Yến đảo Phú Quốc',	'Yến đảo Phú Quốc cực ngon và bổ cho sức khỏe. Yến được cung cấp bởi Tủ thuốc nam tự tin về chất lượng, giá tốt nhất thị trường.',	'Yến đảo Phú Quốc uy tín, chất lượng, giá rẻ nhất thị trường',	'Yến đảo Phú Quốc cực ngon và bổ cho sức khỏe mọi người. Yến được cung cấp bởi Tủ thuốc nam tự tin về chất lượng, giá tốt nhất thị trường.',	4200,	3900,	'5f260-yen_dao_phu_quoc.jpg',	'2018-05-01 23:11:37',	'<p><strong>Yến đảo Phú Quốc</strong> là tổ Yến được nuôi trong động, thường có hình dạng giống cái chén, thân dầy và chân cứng.Yến sào là nguồn tài nguyên quý hiếm của nước ta, một tặng phẩm vô cùng quý giá của thiên nhiên. Đây là một trong tám món ăn cao lương mĩ vị cho các bậc vua chúa.</p>\r\n<p><img class=\"alignnone size-full wp-image-5761\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/yen_sao_dao_phu_quoc.jpg\" alt=\"Yến đảo Phú Quốc\" width=\"552\" height=\"446\" /></p>\r\n<p style=\"text-align: center;\"><em>Yến đảo Phú Quốc món ăn cực kì bổ dưỡng.</em></p>\r\n<h2>Tác dụng của yến đảo Phú Quốc</h2>\r\n<ul>\r\n<li>Yến sào có tác dụng tăng cường hệ miễn dịch.</li>\r\n<li>Giảm biếng ăn, sút cân, suy dinh dưỡng của trẻ nhỏ.</li>\r\n<li>Tái tạo tế bào, giữ mãi sự trẻ trung.</li>\r\n<li>Rất tốt cho người bị mắc các bệnh về đường hô hấp, giảm ho nhanh. Đặc biệt phục hồi nhanh khi phổi bị nhiễm trùng.</li>\r\n<li>Phòng và điều trị các bệnh về gan, tim, tiểu đường...</li>\r\n<li>Phòng chống và điều trị ung thư nhờ có nhiều chất chống oxy hóa.</li>\r\n<li>Giúp giảm căng thẳng, mệt mỏi cho những người lao tâm, lao lực.</li>\r\n</ul>\r\n<h2>Cách phân biệt Yến sào thật và giả</h2>\r\n<p><strong>Cách 1: Ngâm tổ yến vào nước.</strong><br /> + Tổ Yến giả: gặp nước sẽ nở ngay sau 2-3 phút, sau đó sẽ nhão ra.<br /> + Tổ Yến thật: khi ngâm nước sẽ không bị tan và nhão ra.</p>\r\n<p><strong>Cách 2: Đun sôi tổ yến.</strong><br /> + Tổ Yến giả: khi đun có mùi carbonat natri rất hắc, yến bị tan ra. Khi sôi có nhiều bọt, để qua đêm có màu vàng, hôi như bị thiu.<br /> + Tổ Yến thật: khi đun sôi không bị tan ra mà chỉ thành những sợi Yến nguyên vẹn, ít bọt, có mùi đặc trưng của Yến.</p>\r\n<p><strong>Cách 3: Nhỏ thuốc Luigon vào tổ yến khi đun sôi.</strong><br /> + Tổ Yến giả: Nhỏ thuốc vào khi đun sôi có phản ứng tạo màu xanh.<br /> + Tổ Yến thật: Nhỏ thuốc vào khi đun sôi sẽ không có phản ứng tạo màu.</p>\r\n<h2>Cam kết bán hàng</h2>\r\n<ul>\r\n<li>Hàng đủ cân.</li>\r\n<li>Hàng khô hoàn toàn.</li>\r\n<li>Không chất bảo quản, chất độn, phụ gia.</li>\r\n<li>Hoàn lại tiền 200% khi sản phẩm không đúng chất lượng.</li>\r\n</ul>\r\n<h2> </h2>',	'2018-05-05 15:12:59',	1,	1,	',1,',	1,	NULL,	'26',	NULL,	NULL,	'yen-dao-phu-quoc',	NULL,	NULL,	1,	NULL,	5),
(16,	'Hồng sâm củ Hàn Quốc tẩm mật ong',	'Hồng sâm củ Hàn Quốc tẩm mật ong loại 300g / 6củ thơm ngon, bổ dưỡng rất tốt để bồ bổ sức khỏe. Sản phẩm được nhiều người ưu chuộng.',	'Hồng sâm củ Hàn Quốc tẩm mật ong thơm ngon, bổ dưỡng',	'<strong>Hồng sâm củ Hàn Quốc tẩm mật ong</strong> loại 300g / 6củ thơm ngon, bổ dưỡng rất tốt để bồ bổ sức khỏe. Sản phẩm được nhiều người ưu chuộng.',	1550,	NULL,	'ccfe6-hong_sam_cu_han_quoc_tam_mat_ong.jpg',	'2018-05-01 23:16:15',	'<p><strong>Hồng sâm củ Hàn Quốc tẩm mật ong</strong> loại 300g / 6củ được rất nhiều người ưu chuộng với hương vị thơm ngon, dễ dùng. Sản phẩm có tác dụng bồ bổ và chăm sóc sức khỏe cực tốt. Bạn có thể dùng nó để chăm sóc sức khỏe cho mình cũng như làm quà biếu sang trọng tặng người thân, sếp vào những dịp đặc biệt sẽ rất ý nghĩa.</p>\r\n<p><img class=\"alignnone size-full wp-image-5800\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Hồng sâm củ Hàn Quốc tẩm mật ong\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/hong_sam_cu_han_quoc.jpg\" alt=\"Hồng sâm củ Hàn Quốc tẩm mật ong\" width=\"698\" height=\"771\" /></p>\r\n<p style=\"text-align: center;\"><em>Hồng sâm củ Hàn Quốc tẩm mật ong loại 300g / 6củ.</em></p>\r\n<h2>Thành Phần và xuất xứ của Hồng sâm củ Hàn Quốc tẩm mật ong.</h2>\r\n<p><strong>Thành phần cấu tạo</strong>: Hồng sâm 6 năm tuổi, đường hoa quả Fructose, mật ong vừa đủ.</p>\r\n<p><strong>Khối lượng tịnh</strong>: 300g x 6 củ.</p>\r\n<p><strong>Xuất xứ</strong>: Hàn Quốc.</p>\r\n<p><strong>Nhà sản xuất</strong>: DAEDONG KOREA GINSENG CO., LTD.</p>\r\n<h2>Tác dụng của Hồng sâm củ Hàn Quốc tẩm mật ong</h2>\r\n<ul>\r\n<li>Tăng cường miễn dịch, nâng cao thể lực và bồi bổ sức khoẻ cho mọi lứa tuổi.</li>\r\n<li>Giảm căng thảng, mệt mỏi.</li>\r\n<li>Làm tăng chức năng não bộ, giúp phục hồi trí nhớ đang sút giảm.</li>\r\n<li>Làm đẹp da, chống lão hóa tế bào, giúp kéo dài sự sống của tế bào, đẩy mạnh quá trình tạo ra tế bào mới, kéo dài tuổi thọ.</li>\r\n<li>Cải thiện các rối loạn hậu mãn kinh, điều hòa kinh nguyệt.</li>\r\n<li>Cải thiện chức năng sinh lý.</li>\r\n</ul>\r\n<h3>Đối tượng sử dụng</h3>\r\n<p>Dùng cho người gầy yếu, mệt mỏi, thần kinh suy nhược, suy nhược cơ thể, người bị rối loạn hậu mãn kinh.</p>\r\n<h3>Hướng dẫn sử dụng và bảo quản</h3>\r\n<p><strong>Người lớn và trẻ em trên 12 tuổi</strong>: Ngày dùng 1-2 củ, cắt lát ăn trực tiếp.<br /> <strong>Bảo quản</strong>: để nơi khô ráo, thoáng mát, tránh ánh nắng mặt trời.</p>\r\n<h3>Hạn sử dụng</h3>\r\n<p>24 năm kể từ ngày sản xuất. Ngày sản xuất và hạn sử dụng in trên bao bì sản phẩm.</p>',	'2018-05-05 15:12:38',	1,	1,	',1,',	1,	NULL,	'18',	NULL,	NULL,	'hong-sam-cu-han-quoc-tam-mat-ong',	NULL,	NULL,	NULL,	NULL,	24),
(17,	'Sâm maca peru',	'Sâm maca peru giúp tăng cường trí nhớ, trị yếu sinh lí hiệu quả. Chúng tôi tự hào là nơi cung cấp sâm maca peru chính hãng, với giá rẻ nhất.',	'Sâm maca peru tăng cường trí nhớ, trị yếu sinh lí hiệu quả.',	'Sâm maca peru giúp tăng cường trí nhớ, trị yếu sinh lí hiệu quả. Tủ thuốc nam tự hào là nơi cung cấp sâm maca peru chính hãng, với giá rẻ nhất.',	700,	NULL,	'73db9-sam_maca_peru.jpg',	'2018-05-01 23:26:55',	'<p><strong>Sâm maca peru</strong> hay còn gọi là sâm Peru là một loại nhân sâm quý của đất nước Peru thuộc Nam Mỹ. Củ sâm maca có hình dáng phình to và đầu có chóp nhọn dài. Chúng được người dân Peru tìm thấy ở độ cao hơn 4000m so với mặt nước biển. Sức sống của chúng rất mãnh liệt có thể chịu đươc mọi thời tiết khắc nghiệt. Từ hàng nghìn năm trước, các chiến binh Inca đã biết sử dụng sâm Maca để làm tăng sức mạnh trong những trận chiến, giúp họ “bách chiến bách thắng”. Truyền thuyết kể lại rằng các chiến binh Inca sẽ được ăn Maca trước khi bước vào trận chiến. Maca giúp họ mạnh mẽ và dẻo dai hơn người thường. Đây chính là lý do vì sao những chiến Inca binh kêu hùng này luôn “bách chiến bách thắng”.</p>\r\n<p><strong>Sâm maca Peru</strong> có 3 loại: Maca đỏ, Maca vàng và Maca đen. Mỗi loại Maca có một vài chức năng riêng. Nhưng nhìn chung, Maca đen là loại tốt nhất.</p>\r\n<p><img class=\"alignnone size-full wp-image-5805\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/cac_loai_sam_maca_peru.jpg\" alt=\"Các loại sâm maca Peru\" width=\"600\" height=\"212\" /></p>\r\n<p style=\"text-align: center;\"><em>Các loại sâm maca Peru.</em></p>\r\n<h2>Tác dụng của sâm maca Peru</h2>\r\n<ul>\r\n<li>Tăng sức khỏe, sức chịu đựng cho cơ thể.</li>\r\n<li>Nâng cao khả năng sinh lí cho cả nam giới và nữ giới. Thúc đẩy ham muốn tình dục, chức năng tình dục và năng lượng tổng thể.</li>\r\n<li>Cải thiện khả năng sinh sản ở nam giới bằng cách tăng chất lượng và số lượng tinh trùng; khả năng di chuyển của tinh trùng; hỗ trợ điều trị hiệu quả bệnh hiếm muộn.</li>\r\n<li>Hỗ trợ điều trị phì đại tuyến tiền liệt.</li>\r\n<li>Làm giảm các triệu chứng của thời kỳ tiền mãn kinh và mãn kinh như: khô âm đạo, lãnh cảm…</li>\r\n<li>Thúc đẩy cơ thể sản sinh ra nội tiết tố nam testosterone và nội tiết tố nữ estrogen.</li>\r\n<li>Tăng cường sinh lực, giảm căng thẳng, chống mệt mỏi.</li>\r\n<li>Tăng trí nhớ và sự tập trung.</li>\r\n</ul>\r\n<h2>Cách sử dụng sâm maca Peru</h2>\r\n<ul>\r\n<li><strong>Ăn sống</strong>: Sâm Maca có vị ngọt, thơm nên ăn rất dễ. Sau 30 ngày sử dụng, sâm Maca sẽ cho thấy tác dụng rất rõ đối với khả năng sinh lý.</li>\r\n<li><strong>Hãm trà</strong>: Sau khi phơi khô củ Maca, bạn có thể thái thành từng lát mỏng hoặc xay nhuyễn thành bột rồi hãm với nước sôi. Mỗi ngày khoảng 5 – 10g Maca, uống như uống trà.</li>\r\n<li><strong>Hầm với thịt</strong>: Maca Peru 15 – 20g hầm cùng với thịt gà hoặc sườn heo, móng giò heo trong 40 – 60 phút. Chúng ta sẽ có một món ăn hấp dẫn và bổ dưỡng.</li>\r\n<li><strong>Nấu lẩu</strong>: Cho 2 – 3 củ Maca thái lát vào nồi lẩu giúp bổ sung thêm dinh dưỡng cho mọi người.</li>\r\n<li><strong>Ngâm rượu</strong>: dùng 1kg Maca ngâm với 5 – 8 lít rượu 45 độ (tốt nhất là rượu nếp). Sau 30 ngày là có thể dùng được. Mỗi ngày uống 2 – 3 lần trong bữa ăn hoặc trước khi đi ngủ mỗi lần 15 – 30 ml. <em>Lưu ý: Tác dụng có thể thay đổi tùy theo cơ địa mỗi người.</em></li>\r\n</ul>\r\n<h3>Phân biệt Sâm Maca Peru &amp; Sâm Maca Trung Quốc</h3>\r\n<p>Hiện nay có nhiều nơi bán Sâm Maca không đúng Xuất xứ từ Peru vì thế rất không an toàn cho người sử dụng. Tủ thuốc nam sẽ giúp bạn cách phân biệt sâm maca Peru thật.</p>\r\n<p><img class=\"w-100\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/phan_biet_sam_maca_peru.jpg\" alt=\"Cách phân biệt sâm maca Peru\" width=\"779\" height=\"571\" /></p>\r\n<h2>Mua sâm maca peru ở đâu?</h2>\r\n<p>Tủ thuốc nam tự tin là nơi cung cấp sâm maca Peru chất lượng với giá tốt nhất thị trường. Sâm maca của chúng tôi được nhập khẩu 100% tại Peru(không qua trung gian). Ngoài ra khác hàng còn nhận được rất nhiều ưu đãi từ chúng tôi.</p>\r\n<p>- <strong>Giá sâm maca Peru tươi</strong>: <strong>700.000đ</strong>/1kg.</p>\r\n<p>- <strong>Giá sâm maca khô</strong>: <strong>3.000.000đ</strong>/1kg.</p>\r\n<p>- <strong>Giá mua sâm maca khô trên 2kg</strong>:</p>\r\n<p>► <strong>2kg - 4kg</strong> : Giá <strong>2.900.000đ</strong>/kg.<br /> ► <strong>5kg - 10kg</strong> : Giá <strong>2.800.000đ</strong>/kg.</p>',	'2018-05-05 15:11:21',	1,	1,	',1,',	1,	NULL,	'23',	NULL,	NULL,	'sam-maca-peru',	NULL,	1,	NULL,	NULL,	16),
(18,	'Trà nấm chaga',	'Trà nấm chaga được nhập khẩu từ Nga. Sử dung trà nấm chaga hàng ngày giúp trị bệnh tiểu đường, phòng chống ung thư, tăng cường sức khỏe.',	'Trà Nấm chaga - Trị bệnh tiểu đường - Phòng chống ung thư',	'Trà nấm chaga được nhập khẩu từ Nga. Sử dung trà nấm chaga hàng ngày giúp trị bệnh tiểu đường, phòng chống ung thư, tăng cường sức khỏe.',	165,	NULL,	'b34b2-tra-nam-chaga.jpg',	'2018-05-02 09:56:21',	'<p><strong>Trà Nấm chaga</strong> một hộp 30 gói dạng túi lọc được nhập khẩu trực tiếp từ Nga. Sử dụng trà nấm chaga thường xuyên giúp trị bệnh tiểu đường, phòng chống ung thư, giảm stress, tăng cường sức khỏe.</p>\r\n<p><img class=\"size-full wp-image-5811 aligncenter\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/hop_tra_nam_chaga.jpg\" alt=\"Hộp trà nấm chaga\" width=\"726\" height=\"500\" /></p>\r\n<p style=\"text-align: center;\"><em>Trà nấm chaga.</em></p>\r\n<p><strong>Thành phần</strong>: 100% nấm Chaga nguyên chất nhập khẩu từ Siberia, Nga.</p>\r\n<h2>Tác dụng của trà nấm chaga</h2>\r\n<ul>\r\n<li>Điều trị bệnh tiểu đường.</li>\r\n<li>Phòng chống ung thư.</li>\r\n<li>Ngăn ngừa cao huyết áp và giảm cholesterol.</li>\r\n<li>Chống lão hóa.</li>\r\n<li>Ngăn chặn có hiệu quả sự lây truyền của vi rút gây bệnh.</li>\r\n<li>Phòng và hỗ trợ điều trị HIV-AIDS do tăng cường hệ miễn dịch.</li>\r\n<li>Cải thiện và phòng ngừa dị ứng vỏ não.</li>\r\n<li>Hỗ trợ điều trị các bệnh viêm gan, viêm dạ dày, viêm thận với hiệu quả điều trị rất tốt.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng</h2>\r\n<p>Mọi người đều có thể sử dụng trà giúp cơ thể khỏe mạnh và dồi dào năng lượng.</p>\r\n<h3>Cách dùng trà Nấm chaga</h3>\r\n<p>Pha trà nấm chaga uống thay trà hoặc nước uống hàng ngày.<br /> <strong>Cách pha trà nóng</strong>: Cho 1 gói trà vào hãm với 500ml nước sôi trong 2-3 phút. Rót nước ra ly và dùng dần trong ngày.<br /> <strong>Cách pha trà lạnh</strong>: Cho 1 gói trà vào hãm với 500ml nước sôi trong 2-3 phút. Cho thêm đường (tùy thích) để ngăn mát tủ lạnh. Rót nước ra ly và dùng dần trong ngày.</p>',	'2018-05-05 15:10:48',	1,	1,	',1,',	1,	NULL,	'23',	NULL,	NULL,	'tra-nam-chaga',	NULL,	NULL,	NULL,	NULL,	6),
(19,	'Trà Sâm Ngọc Linh',	'Trà sâm Ngọc Linh hòa tan. Trà sâm số 1 cho sức khỏe giúp bồ bổ nguyên khí, tăng cường sức khỏe, tạo sự tỉnh táo và tập chung.',	'Trà sâm ngọc linh - Trà sâm số cực tốt cho sức khỏe.',	'<strong>Trà sâm Ngọc Linh</strong> hòa tan. Trà sâm số 1 cho sức khỏe giúp bồ bổ nguyên khí, tăng cường sức khỏe, tạo sự tỉnh táo và tập chung.',	500,	NULL,	'72001-tra-sam-ngoc-linh.jpg',	'2018-05-02 10:06:11',	'<p><a href=\"http://school.com/san-pham/sam-ngoc-linh.html\">Sâm ngọc linh</a> là nhân sâm quý hàng đầu trên thế giới. Vì vậy giá thành để mua nó khá cao, không phải ai cũng mua được. Tủ thuốc nam sẽ giúp bạn sử dụng sâm ngọc linh với chi phí bỏ ra chỉ 500 nghìn đồng. Với số tiền này bạn có thể mua được<em> trà sâm ngọc linh</em> của chúng tôi, với thành phần chính là sâm ngọc linh.</p>\r\n<p><img class=\"alignnone size-full wp-image-5833\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/tra-sam-ngoc-linh-2.jpg\" alt=\"Trà sâm ngọc linh\" width=\"864\" height=\"486\" /></p>\r\n<p style=\"text-align: center;\"><em>Trà sâm ngọc linh</em></p>\r\n<h2>Thành phần trong trà sâm ngọc linh</h2>\r\n<p>Thành phần chính là sâm ngọc linh. Ngoài ra còn có thêm đường, hương liệu vừa đủ. Không dùng phụ gia thực phẩm.</p>\r\n<h2>Tác dụng</h2>\r\n<ul>\r\n<li>Bổ bổ nguyên khí.</li>\r\n<li>Tăng cường sức đề kháng.</li>\r\n<li>Giúp tỉnh táo, tập trung.</li>\r\n<li>Kích thích ăn uống, giúp cơ thể nhập thụ chất dinh dưỡng tốt hơn.</li>\r\n<li>Kích thích não bộ, tăng cường trí nhớ.</li>\r\n<li>Phòng chống bệnh ung thư.</li>\r\n<li>Chống lão hóa.</li>\r\n</ul>\r\n<h2>Cách sử dụng</h2>\r\n<p>Nhai trực tiếp hoặc hòa tan 1 gói trà sâm Ngọc Linh vào cốc, đổ khoảng 200ml nước sôi dưới 60 độ C, đậy kín cốc hãm trong 2-3 phút, có thể thêm ít đường phèn.</p>\r\n<p><strong>Người lớn</strong>: uống 3-4 gói/ngày, T<strong>rẻ em</strong>: uống 1-2 gói/ngày.</p>\r\n<p><strong>Đối tượng sử dụng</strong>:<br /> - Người bị suy nhược cơ thể, mệt mỏi, sức đề kháng kém.<br /> - Người lao động nặng nhọc, kém tập trung, người làm việc căng thẳng, quá sức hoặc người mới ốm dậy.</p>\r\n<p><strong>Bảo quản</strong>: Để nơi khô mát, tránh ánh sáng trực tiếp, nhiệt độ dưới 30 độ C, để xa tầm tay trẻ em.</p>\r\n<p><strong>Lưu ý</strong>: <strong><em>Không dùng trà sâm ngọc linh cho phụ nữ mang thai, đang cho con bú.</em></strong></p>',	'2018-05-06 01:14:09',	1,	1,	',1,',	1,	NULL,	'17',	NULL,	NULL,	'tra-sam-ngoc-linh',	NULL,	NULL,	NULL,	NULL,	26),
(20,	'Viên ngậm Sâm Ngọc Linh',	'Viên ngậm sâm ngọc linh được điều chế từ củ và rẽ của sâm ngọc linh. Nó chứa đầy dủ các hoạt chất của sâm ngọc linh - sâm tốt nhất thế giới.',	'Viên ngậm sâm ngọc linh - Sâm ngậm sâm việt nam',	'<strong>Viên ngậm sâm ngọc linh</strong> được điều chế từ củ và rẽ của <a href=\"http://school.com/san-pham/sam-ngoc-linh.html\">sâm ngọc linh</a>. Nó chứa đầy dủ các hoạt chất của sâm ngọc linh - nhân sâm tốt nhất thế giới.',	120,	NULL,	'607ee-vien_ngam_sam_ngoc_linh.jpg',	'2018-05-02 10:12:01',	'<p><strong>Viên ngậm Sâm Ngọc linh</strong> chứa các hoạt chất toàn phần của Sâm Ngọc Linh. Viên ngậm chứa đầy đủ 15 hợp chất Saponin: Ginsenosid Rb1, Rd, Rg1, Majonosid R2, Pseudoginsenosid RT4 và 10 K5VN-PANAXOSID khác. Ngoài ra còn chứa 14 ACID béo, có đủ 8 acid amin cần thiết cho cơ thể và 20 NGUYÊN TỐ VI LƯỢNG. Đặc biệt các nguyên tố có giá trị sinh học: K, Na, Ca, Mg, Fe, Mn, Cu, Co, Zn, B, Se, As…</p>\r\n<p><img class=\"alignnone size-full wp-image-5851\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/vien_ngam_sam_ngoc_linh_1.jpg\" alt=\"Viên ngậm sâm ngọc linh\" width=\"238\" height=\"296\" /></p>\r\n<p style=\"text-align: center;\"><em>Viên ngậm sâm ngọc linh</em></p>\r\n<h2>Dạng bào chế</h2>\r\n<ul>\r\n<li>Bào chế dạng viên ném. Viên màu vàng nâu, mùi thơm đặc biệt, vị ngọt hơi đắng.</li>\r\n<li>Viên được đóng trong vỉ nhôm, 10 viên/1 vỉ, 3 vỉ/hộp.</li>\r\n</ul>\r\n<h2>Viên ngậm sâm ngọc linh có tác dụng gì?</h2>\r\n<ul>\r\n<li>Trị suy nhược cơ thể, suy nhược thần kinh.</li>\r\n<li>Nâng cao sức khỏe, độ dẻo dai.</li>\r\n<li>Tăng cường trí nhớ và thị lực.</li>\r\n<li>Giảm mệt mỏi trong lao động (đặc biệt tốt cho người lớn tuổi, người lao động trí óc, diễn viên, vận động viên thể dục thể thao… khi làm việc căng thẳng, quá sức).</li>\r\n<li>Làm giảm viêm họng hạt mãn tính (không cần dùng kháng sinh), giảm ho, khàn tiếng, rát cổ.</li>\r\n<li>Trị bệnh huyết áp thấp gây mệt mỏi, chóng mặt, ngất xỉu.</li>\r\n<li>Giảm bệnh đái đường. Làm tăng tác dụng của thuốc trị bệnh đái đường (sulfamid hạ đường huyết, insulin).</li>\r\n<li>Giảm đau và viêm loét dạ dày.</li>\r\n<li>Làm long đờm, dễ thở và giảm tái phát cơn hen phế quản mãn.</li>\r\n<li>Tốt cho sin lí nam giới cũng như nữ giới.</li>\r\n</ul>\r\n<h2>Cách dùng viên ngậm sâm ngọc linh</h2>\r\n<p>Ngậm cho đến khi viên tan hết để trị viêm họng. Ngoài ra, có thể uống (nếu không thích ngậm). Người lớn: 1 – 2 viên/ngày.<br /> Trẻ em: nửa liều người lớn.<br /> - <strong>Đối với bệnh hen suyễn</strong>: cần ngậm thuốc khi thấy có triệu chứng sắp lên cơn hen để chặn trước.<br /> - <strong>Đối với bệnh viêm họng hạt mãn</strong>: gây kích thích ở họng làm khó ngủ, nhiều đờm, có thể ngậm 1 viên trước khi ngủ sẽ làm giảm đờm và thấy dễ chịu khi thức giấc.<br /> - <strong>Đối với người bị bệnh đái đường</strong>: có thể dùng phối hợp 1 – 2 viên thuốc với 1/2 liều điều trị của sulfamid hạ đường huyết sẽ có tác dụng bằng nguyên liều điều trị của thuốc sulfamid hạ đường huyết.</p>\r\n<h2>Bảo Quản</h2>\r\n<p>Bảo quản nơi khô ráo, thoáng mát.<br /> <strong>Viên ngậm Sâm ngọc linh</strong> có thể ngả màu nâu sậm (do hút ẩm) nhưng không ảnh hưởng đến tác dụng của Sâm Ngọc Linh.</p>\r\n<p><strong>Hiện tại viên ngậm sâm ngọc linh của chúng tôi đã hết hàng. Sản phẩm thay thế viên ngậm sâm ngọc linh là <a href=\"http://tuthuocnam.com/san-pham/tra-sam-ngoc-linh.html\">Trà sâm ngọc linh</a>.</strong></p>',	'2018-05-05 15:10:24',	1,	1,	',1,',	1,	NULL,	'17',	NULL,	1,	'vien-ngam-sam-ngoc-linh',	2,	NULL,	NULL,	NULL,	5),
(21,	'An cung ngưu hoàng hoàn hiệu rùa vàng',	'An Cung Ngưu Hoàng Hoàn Rùa Vàng điều trị đột quỵ não, ngăn ngừa hiện tượng tai biến, xuất huyết não, phục hồi cho bệnh nhân tai biến.',	'An cung ngưu hoàng hoàn rùa vàng - Điều trị đột quỵ não',	'<strong>An Cung Ngưu Hoàng Hoàn Rùa Vàng</strong> điều trị đột quỵ não, ngăn ngừa hiện tượng tai biến, xuất huyết não, phục hồi cho bệnh nhân tai biến.',	1400,	NULL,	'6e4a8-an_cung_nguu_dieu-tri_dot_quy_nao.jpg',	'2018-05-02 10:18:28',	'<p><strong>An Cung Ngưu Hoàng Hoàn Rùa Vàng</strong> là sản phẩm của Công ty TNHH Dược Thường Thái Diên An, tỉnh Thiểm Tây, Trung Quốc, được nhập khẩu về Việt Nam. Thuốc được Cục Quản lý Dược, Bộ Y tế cấp phép lưu hành theo tiêu chuẩn CP 2005. Số giấy phép lưu hành sản phẩm VN-10650-1.0. Số giấy tiếp nhận hồ sơ đăng ký tài liệu thông tin thuốc của Cục quản lý dược - Bộ Y tế 0757/11/QLD-TT ngày 09/8/2011. Đây là sản phẩm có tác dụng rất tốt trong việc ngăn ngừa và điều trị bệnh tai biến mạch máu não. Nó được sử dụng ở hầu hết các bệnh viện tuyến Trung ương của Việt Nam.</p>\r\n<p><img class=\"alignnone size-full wp-image-5860\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/an_cung_nguu.jpg\" alt=\"An cung ngưu hoàng hoàn rùa vàng điều trị đột quỵ não\" width=\"848\" height=\"617\" /></p>\r\n<p style=\"text-align: center;\"><em>An cung ngưu hoàng hoàn rùa vàng điều trị đột quỵ não</em></p>\r\n<p><strong>Nhà sản xuất</strong>: Yanan changtai Pharmaceutical.,Ltd</p>\r\n<p><strong>Hạn sử dụng</strong>: 2 năm tính từ khi ngày sản xuất.</p>\r\n<h2>Thành phần thuốc</h2>\r\n<ul>\r\n<li>Ngưu hoàng nuôi cấy: 166.67 mg.</li>\r\n<li>Bột sừng trâu cô đặc: 333.33 mg.</li>\r\n<li>Xạ hương: 41.67 mg.</li>\r\n<li>Trân châu: 83.33 mg.</li>\r\n<li>Chu sa: 166.67 mg.</li>\r\n<li>Hùng hoàng: 166.67 mg.</li>\r\n<li>Hoàng liên: 166.67 mg.</li>\r\n<li>Hoàng cầm: 166.67 mg.</li>\r\n<li>Chi tử: 166.67 mg.</li>\r\n<li>Uất kim: 166.67 mg.</li>\r\n<li>Băng phiến: 41.67 mg.</li>\r\n<li>Tá dược bao gồm: Sáp ong, mật ong tinh luyện, dầu đậu nành thành phần vừa đủ 1 viên hoàn mềm.</li>\r\n</ul>\r\n<h2>Tác dụng của an cung ngưu hoàng hoàn rùa vàng</h2>\r\n<h3>An Cung Rùa Vàng có tác dụng phục hồi liệt cho bệnh nhân đột quỵ não</h3>\r\n<p>- Theo nhóm nghiên cứu của Nguyễn Minh Hiện - Bệnh viện Quân y 103, Nguyễn Minh Hà, Nguyễn Công Thực - Viện Y học Cổ truyền Quân đội cho thấy bệnh nhân đột quỵ nhồi máu não cấp giảm liệt rõ rệt.<br /> - Mức độ liệt theo thang điểm Henry: Nhóm được kết hợp điều trị thuốc An Cung liệt tay hoàn toàn trước điều trị là 53,65%, sau điều trị 12,18%. Liệt chân hoàn toàn trước điều trị: 46,33 %, sau điều trị 7,31%. So sánh kết quả điều trị với nhóm chứng, sự hồi phục rõ rệt hơn (P&lt;0,01).<br /> - Mức độ phục hồi theo thang điểm NIHSS: Điểm trung bình trước điều trị của nhóm được kết hợp thuốc An Cung Ngưu Hoàng Hoàn là 7,54 +_3,62, sau điều trị là 3,2 +_2,58 (P&lt;0,001), so sánh kết quả điều trị với nhóm chứng, sự phục hồi rõ rệt hơn (P&lt;0,05).</p>\r\n<h3>An Cung Ngưu Hoàng Hoàn Rùa Vàng có độc tính thấp nên có thể sự dụng an toàn trong điều trị</h3>\r\n<p>Theo kết quả nghiên cứu của Nguyễn Minh Hà, trên động vật thực nghiệm với liều 7,5gr/kg trọng lượng chuột (liều tối đa chuột có thể uống được), chưa xác định được liều gây chết 50% động vật thí nghiệm.</p>\r\n<h3>An Cung Ngưu Hoàng Hoàn tác dụng trên bệnh nhân xuất huyết não</h3>\r\n<p>An Cung Ngưu Hoàng Hoàn có tác dụng thanh tâm giải độc, thông đàm, bình can tức phong, tỉnh não khai khiếu. Thuốc làm cải thiện tình trạng hôn mê, liệt nửa người, thất ngôn.</p>\r\n<h2>Cơ chế tác động của An Cung Hoàng Hoàn Rùa Vàng</h2>\r\n<ul>\r\n<li>Thuốc an Cung Rùa Vàng khi đi vào cơ thể làm tan cục máu đông.</li>\r\n<li>Kích thích phục hồi vùng não bị tổn thương, vùng não hấp hối đang chết.</li>\r\n<li>An thần và giảm tiêu thụ oxy trong não. Giữ cho não hoạt động ở môi trường thiếu oxy mà sống được lâu nhất.</li>\r\n<li>Cơ chế phục hồi não làm cho bệnh nhân không bị liệt cơ tròn, giúp khả năng tiểu tiện tự chủ hơn.</li>\r\n<li>Phục hồi dây thần kinh màn hầu, giúp nói năng dễ hơn, không bị nói ngọng, nói lắp do méo miệng.</li>\r\n<li>Thuốc giúp bệnh nhân tỉnh táo hơn, tăng cường tưới máu bù, làm giãn mạch máu bị hẹp, giúp dòng máu lưu thông ổn định.</li>\r\n</ul>\r\n<h2>Dùng an cung ngưu hoàng hoàn rùa vàng trong trường hợp nào?</h2>\r\n<ul>\r\n<li>Xuất huyết não.</li>\r\n<li>Nhồi máu não</li>\r\n<li>Viêm não</li>\r\n<li>Sốt cao, co giật do rối loạn trung tâm điều nhiệt ở thân não (trường hợp này dùng Acetaminnophen không kết quả)</li>\r\n<li>Dùng thuốc An Cung Rùa Vàng để phục hồi di chứng sau tai biến mạch máu não:</li>\r\n<li>Dùng cho những bệnh nhân sau khi cấp cứu qua giai đoạn nguy kịch do tai biến mạch máu não (hay đột quỵ não), đang trong giai đoạn phục hồi chức năng sinh lý.</li>\r\n<li>Sau khi bị bệnh, bệnh nhân rơi vào tình trạng:<br /> - Tinh thần không tỉnh táo, lơ mơ khó nhận biết.<br /> - Rối loạn ngôn ngữ: Không nói được, nói ú ớ không rõ tiếng, hiểu được, không trình bày được.<br /> - Đái ỉa dầm dề không tự chủ được.<br /> - Các chức năng vận động chưa hồi phục.</li>\r\n</ul>\r\n<h2>Cách sử dụng</h2>\r\n<p>Dùng đường uống:<br /> - Người lớn: 1 viên/lần, 1 lần/ngày<br /> - Trẻ em dưới 3 tuổi: ¼ viên/lần, 1 lần/ngày<br /> - Từ 4 - 6 tuổi: ½ viên/lần, 1 lần/ngày<br /> Liều dùng để phòng:<br /> - Thông thường 1 tháng/viên.<br /> - Tùy thể trạng của bệnh nhân mà điều chỉnh liều cho phù hợp.<br /> - Bệnh nhân khi có nhu cầu điều trị bằng an cung ngưu hoàng hoàn chúng tôi sẽ tư vấn trực tiếp về liều dùng và cách sử dụng hợp lý.</p>\r\n<h2>Chống chỉ định</h2>\r\n<ul>\r\n<li>Người bị đột quỵ, tai biến mạch máu não có hạ thân nhiệt, tụt huyết áp, xuất huyết toàn bộ não thì không nên dùng An Cung Rùa Vàng.</li>\r\n<li>Không dùng cho phụ nữ có thai và cho con bú.</li>\r\n<li>Thận trọng với người suy thận.</li>\r\n<li>Không dùng quá liều chỉ định.</li>\r\n</ul>',	'2018-05-05 15:11:53',	1,	1,	',1,',	1,	NULL,	'23,24',	NULL,	NULL,	'cung-nguu-hoang-hoan-hieu-rua-vang',	NULL,	NULL,	NULL,	NULL,	12),
(22,	'Vũ Hoàng thanh tâm',	'Vũ Hoàng Thanh Tâm giúp phòng bệnh đột quỵ, cao huyết áp hiệu quả. Sản phẩm được nhập khẩu từ công ty Kwang Dong Pharmaceutical, Hàn quốc.',	'Vũ hoàng thanh tâm - An cung ngưu Hàn Quốc - Phòng bệnh đột quỵ',	'<strong>Vũ Hoàng Thanh Tâm</strong> giúp phòng bệnh đột quỵ não, cao huyết áp hiệu quả. Sản phẩm được nhập khẩu từ công ty Kwang Dong Pharmaceutical, Hàn quốc.',	1400,	NULL,	'8ebba-vu-hoang-thanh-tam.jpg',	'2018-05-02 10:22:37',	'<p><strong>Vũ hoàng thanh tâm</strong> là thực phẩn chức năng được nhiều người tin dùng trong dòng an cung ngưu Hàn Quốc. Sản phẩm này hỗ trợ điều trị các bệnh cao huyết áp, tai biến mạch máu não, thiếu máu…Ngoài ra, nó còn dùng cho những người cơ thể bị mệt mỏi, bất an, tinh thần không ổn định.</p>\r\n<p><img class=\"alignnone size-full wp-image-5866\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/chi-tiet-vu-hoang-thanh-tam.jpg\" alt=\"Chi tiết vũ hoàng thanh tâm\" width=\"454\" height=\"320\" /></p>\r\n<p style=\"text-align: center;\"><em>Vũ Hoàng Thanh Tâm</em></p>\r\n<p><strong>Quy cách đóng gói</strong>: 10 viên/hộp, mỗi viên 3,75g .<br /> <strong>Số đăng ký</strong>: 9139/2013/ATTP-XNCB<br /> <strong>Sản xuất tại</strong>: KWANG DONG PHARMACEUTICAL CO., LTD<br /> <strong>Nhập khẩu</strong>:CÔNG TY TNHH SẢN XUẤT &amp; THƯƠNG MẠI NAM SƠN<br /> <strong>Bảo quản</strong>: Nơi khô ráo, tránh ánh sáng.</p>\r\n<p> </p>\r\n<h2>Thành phần của vũ hoàng thanh tâm</h2>\r\n<p>Củ Hoài sơn 282 mg, Cam thảo 202mg, Bồ hoàng 100 mg, Thần khúc 100mg, Nhân sâm 97 mg, Giá đậu nành 70mg,Vỏ quế 70mg, A giao 70mg, Rễ Hoàng cầm 60mg, Bạch truật 60mg, Bạch thược 60mg, Đương quy 60mg, Phòng phong 60mg, Mạch môn 60mg, Sài hồ 50mg, Rễ Cát cánh 50mg, Quả Mơ 50mg, Phục linh 50mg, Xuyên khung 50mg, Ngưu hoàng 14mg, Long não 41mg, Linh dương giác 38mg, Gừng 30mg, Bạch liễm 30mg, mật ong, glycerin, lactose, cellulose vi tinh thể, calci panthenat vừa đủ viên.</p>\r\n<h2>Tác dụng của Vũ Hoàng thanh tâm</h2>\r\n<ul>\r\n<li>Giúp bổ nguyên khí, hoạt huyết thông mạch, ổn định huyết áp, ngăn ngừa nguy cơ đột quỵ do bệnh tim mạch.</li>\r\n<li>Hỗ trợ điều trị di chứng tai biến mạch máu não, thiểu năng tuần hoàn não, rối loạn tiền đình, suy giảm trí nhớ.</li>\r\n<li>Giảm căng thẳng, mệt mỏi cho người lao động trí óc với cường độ cao.</li>\r\n<li>Phòng chống bệnh cao huyết áp.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng</h2>\r\n<ul>\r\n<li>Người bị cao huyết áp.</li>\r\n<li>Bệnh nhân bị biến chứng do tai biến mạch máu não như: đờm ứ trệ gây khó thở, tê liệt cơ mặt, rối loạn ngôn ngữ.</li>\r\n<li>Người bị huyết áp thấp.</li>\r\n<li>Người tim đập nhanh, rối loạn thần kinh thực vật.</li>\r\n<li>Người bị rối loạn tiền đình, suy giảm trí nhớ.</li>\r\n<li>Người cao tuổi hoặc người làm việc trí óc căng thẳng.</li>\r\n</ul>\r\n<h2>Cách dùng Vũ Hoàng Thanh Tâm</h2>\r\n<p>Nhai trực tiếp, hoặc nghiền ra uống với nước ấm sau khi ăn cơm.</p>\r\n<p><strong>Người lớn</strong>: ngày uống 1 lần mỗi lần 1 viên.</p>\r\n<p>Đối với bệnh nhân bị tai biến mạch máu não thì nên dùng từ 5-10 hộp. còn đối với các trường hợp khác như bệnh tim mạch, rối loạn tiền đình thì nên dùng từ 3-6 hộp.</p>\r\n<p><strong>Lưu ý khi sử dụng:</strong></p>\r\n<p>Đối với những trường hợp sau, khi sử dụng cần tham khảo ý kiến của bác sĩ hoặc dược sĩ:</p>\r\n<ul>\r\n<li>Sử dụng với mục đích hạ huyết áp.</li>\r\n<li>Bệnh nhân hoặc người già bị huyết áp cao.</li>\r\n<li>Bệnh nhân mắc bệnh về tim hoặc thận.</li>\r\n<li>Bệnh nhân bị phù thũng.</li>\r\n<li>Bệnh nhân đang được bác sĩ điều trị.</li>\r\n</ul>',	'2018-05-05 15:09:34',	1,	1,	',1,',	1,	NULL,	'23,24',	NULL,	NULL,	'vu-hoang-thanh-tam',	NULL,	NULL,	NULL,	NULL,	19),
(23,	'Cây giống sâm Ngọc Linh',	'Tủ thuốc nam chuyên cung cấp cây giống sâm ngọc linh với giá tốt nhất thị trường. Cây giống đảm bảo, uy tín, chất lượng.',	'Cây giống Sâm Ngọc Linh giá tốt nhất thị trường.',	'Tủ thuốc nam chuyên cung cấp cây giống sâm ngọc linh với giá tốt nhất thị trường. Cây giống đảm bảo, uy tín, chất lượng.',	NULL,	NULL,	'b89f7-cay_giong_sam_ngoc_linh.jpg',	'2018-05-02 10:31:15',	'<p><a title=\"Sâm Ngọc Linh\" href=\"http://tuthuocnam.com/san-pham/sam-ngoc-linh.html\">Sâm ngọc linh</a> được đánh giá là loại sâm tốt hàng đầu thế giới. Nó đặc biệt tốt cho sức khỏe của con người. Để bảo tồn loại sâm vô cùng quý giá của Việt Nam này chúng tôi ươn trồng và cung cấp cây giống Sâm Ngọc Linh trên toàn quốc.</p>\r\n<p><img class=\"alignnone size-full wp-image-5887\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/cay-sam-ngoc-linh.jpg\" alt=\"Cây sâm Ngọc Linh\" width=\"404\" height=\"141\" /></p>\r\n<p style=\"text-align: center;\"><em>Cây sâm ngọc linh</em></p>\r\n<h2>Tác dụng của Sâm Ngọc Linh</h2>\r\n<p>Sâm ngọc linh được nghiên cứu đánh giá là chứa rất nhiều dược chất, thành phần quý có giá trị đặc biệt tốt cho sức khỏe con người. Các nhà khoa học cho rằng sâm ngọc linh có nhiều ưu điểm vượt trội hơn các loại sâm khác giới như sâm Hàn Quốc, sâm Trung Quốc, sâm Mỹ,…Theo nghiên cứu thì sâm ngọc linh có công dụng lớn trong:</p>\r\n<ul>\r\n<li>Phòng và điều trị ung thư.</li>\r\n<li>Kích thích não bộ, tăng cường trí nhớ.</li>\r\n<li>Chống lão hóa.</li>\r\n<li>Tăng cường chức năng sinh lý.</li>\r\n<li>Tăng cường sức đề kháng.</li>\r\n<li>Chống suy nhược cơ thể, suy nhược thần kinh</li>\r\n<li>Đặc biệt tốt cho người bệnh đang trong quá trình điêu trị ung thư.</li>\r\n</ul>\r\n<p><em>Qua các đánh giá của những chuyên gia thì sâm ngọc linh chính là sản phẩm giúp bồi bổ cơ thể một cách toàn diện</em>.</p>\r\n<h2>Mua cây giống Sâm Ngọc linh ở đâu?</h2>\r\n<p>Sâm ngọc linh hiện nay được bán trên thị trường với mức giá rất cao. Chúng được bán cao hơn cả những loại sâm nhập khẩu khác. Thế nhưng, nguồn cung vẫn chưa thể đủ để đáp ứng được nhu cầu sử dụng người tiêu dùng. Một điều đặc biệt nữa sâm ngọc linh là loại thảo dược mang lại giá trị kinh tế rất cao cho người trồng. Chính vì vậy mà ngày càng có nhiều người nuôi trồng cung cấp cho thị trường.</p>\r\n<p>Trên thị trường hiện nay có nhiều đơn vị phân phối cây giống sâm ngọc linh. Chúng tôi tự tin là đơn vị cung cấp <em>cây giống sâm Ngọc Linh</em> tốt nhất cho thị trường, phục vụ quý khách muốn nuôi trồng loại thảo dược quý hiếm này.</p>\r\n<h2>Tại sao chọn mua cây giống Sâm Ngọc Linh của chúng tôi?</h2>\r\n<p>Chúng tôi thu mua các hạt giống tốt nhất từ đồng bào dân tộc và người dân sống tại nơi có sâm ngọc linh sinh trưởng một cách tự nhiên tại 2 tỉnh là Kon Tum và vùng Trà My thuộc tỉnh Quảng Nam. Nhờ vậy mà khả năng sinh trưởng và phát triển của cây giống là rất tốt. Như quá trình ươm hạt bình thường thì luôn có tỷ lệ sống và phát triển vào khoảng 90%.</p>\r\n<p>Tất cả các cây giống sâm ngọc linh của chúng tôi được ươm và chăm sóc ngay tại những cánh rừng già. Đây là môi trường sống tự nhiên để cây sâm có thể sinh trưởng và phát triển tốt. Trước khi đưa ra thị trường, các cây giống đều được tuyển chọn rất kỹ lưỡng. Chúng tôi luôn mong muốn mang lại những cây giống tốt nhất cho khách hàng. Giúp cho người mua có được cây giống khỏe mạnh và đạt tiêu chuẩn nhất.</p>\r\n<p>Cây giống sâm ngọc linh chúng tôi có chứa đầy đủ 3 hợp chất saponin có trong sâm ngọc linh là ginsenosid Rg1 (G-Rg1), ginsenosid Rb1 (G-Rb1) và Majonosid R2 (MR2).</p>',	'2018-05-05 15:09:11',	1,	1,	',1,',	1,	NULL,	'17',	NULL,	NULL,	'cay-giong-sam-ngoc-linh',	NULL,	NULL,	NULL,	NULL,	7),
(24,	'Đông trùng hạ thảo Tây Tạng',	'Đông trùng hạ thảo Tây Tạng thảo dược quý cực tốt cho sức khỏe. Địa chỉ cung cấp Đông trùng hạ thảo uy tín, chất lượng tại Hà Nội.',	'Đông trùng hạ thảo Tây Tạng uy tín, giá tốt.',	'<strong>Đông trùng hạ thảo Tây Tạng</strong> thảo dược quý cực tốt cho sức khỏe, giúp chữa các chứng bệnh như phế hư khái suyễn, liệt dương, di tinh, lưng gối đau mỏi…',	NULL,	NULL,	'17aaf-dong_trung_ha_thao_tay_tang.jpg',	'2018-05-02 10:38:27',	'<h2>Đông trùng hạ thảo Tây Tạng là gì?</h2>\r\n<p><strong>Đông trùng hạ thảo</strong> bản chất là một dạng ký sinh của của một loại nấm trên cơ thể của ấu trùng thuộc họ bướm. Gọi Đông trùng hạ thảo xuất phát từ quan sát thực tế khi vào mùa hè nấm mọc chồi từ đầu con sâu nhô lên khỏi mặt đất. Vào mùa đông thì cặp cá tể này giống như con sâu (côn trùng) vào mùa hè thì chúng giống như một loại thực vật thảo mộc hơn.</p>\r\n<p><strong>Đông trùng hạ thảo Tây Tạng</strong> là đông trùng hạ thảo được tìm thấy trên một số cao nguyên ở Tây Tạng. Khi thời tiết bắt đầu sang hè dân du mục Tây Tạng phơi mình trên những dãy núi để tìm đông trùng hạ thảo.</p>\r\n<p><img class=\"alignnone size-full wp-image-5893\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Đông trùng hạ thảo Tây Tạng\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/dong_trung_ha_thao.jpg\" alt=\"Đông trùng hạ thảo Tây Tạng\" width=\"400\" height=\"282\" /></p>\r\n<p style=\"text-align: center;\"><em>Đông trùng hạ thảo Tây Tạng</em></p>\r\n<h2>Công dụng của Đông trùng hạ thảo Tây Tạng</h2>\r\n<p>Theo các sách Đông Y từ ngày xưa thì Đông trùng hạ thảo vị ngọt, tính ấm, có công năng dưỡng phế, bổ thận, ích tinh. Vì vậy chúng thường được dùng để chữa các chứng bệnh như phế hư khái suyễn, thận suy dương nuy (liệt dương), di tinh, lưng gối đau mỏi…</p>\r\n<h2>Cách sử dụng</h2>\r\n<p><strong>1. Nhai sống trực tiếp</strong><br /> Cách sử dụng đông trùng hạ thảo đơn giản nhất là nhai sống trực tiếp. Khi dùng bạn lấy một bát nước ấm khoảng 30 độ rửa sạch con trùng thảo. Sau đó cho vào 1 bát nước nhiệt độ khoảng 60 độ ngâm trong vòng 2 phút, lấy ra nhai trực tiếp. Chú ý về vấn đề vệ sinh để tránh lây nhiễm ký sinh trùng.</p>\r\n<p><strong>2. Đông trùng hạ thảo ngâm rượu</strong><br /> Ngâm rượu là một trong những cách sử dụng được lâu và nhiều lần. Cách này rất thích hợp với các quý ông. Lấy đông trùng đem ngâm với rượu, sau 3 tháng là có thể dùng được. Có thể ngâm cùng với một số loại thảo dược khác như nhân sâm, nhung hươu... Lưu y mỗi ngày chỉ nên sử dụng 1-2 ly rượu, không được dùng quá nhiều.</p>\r\n<p><strong>3. Đông trùng hạ thảo hầm gà</strong><br /> Mua 1 con gà ác nhồi đông trùng hạ thảo, nhân sâm, kỳ tử, táo đỏ và hầm đến khi chín là có thể ăn được. Mỗi tuần ăn 1 lần là tốt nhất.</p>\r\n<p><strong>4. Nấu cháo đông trùng hạ thảo</strong><br /> Cháo đông trùng hạ thảo rất thích hợp cho người ốm, người già răng yếu hay người bệnh chán ăn. Cách làm rất đơn giản chỉ cần lấy đông trùng hạ thảo nguyên con tán thành bột. Sau khi cháo nhừ trộn bột vào cháo rồi ăn lúc nóng là tốt nhất.</p>',	'2018-05-05 15:08:51',	1,	1,	',1,',	1,	1,	'22',	NULL,	NULL,	'dong-trung-ha-thao-tay-tang',	NULL,	NULL,	NULL,	NULL,	14),
(25,	'Vi cá mập',	'Vi cá mập thực phẩm bổ dưỡng giúp bạn sống khỏe mỗi ngày. Tủ thuốc nam chuyên cung cấp vi cá mập với giá tốt nhất thị trường.',	'Vi cá mập uy tín, chất lượng, giá tốt nhất thị trường',	'<strong>Vi cá mập</strong> thực phẩm bổ dưỡng giúp bạn sống khỏe mỗi ngày. Sử dụng nó thường xuyên giúp ngăn chặn sự hình thành và phát triển của ung thư. Ngoài ra nó còn rất tốt cho mắt và xương khớp.',	NULL,	NULL,	'61d46-vi_ca_map.jpg',	'2018-05-02 11:06:58',	'<h2>Vi cá mập là gì?</h2>\r\n<p><strong>Vi cá mập</strong> (sụn cá mập) là sụn được chiết xuất từ vây cá mập. Nó có chứa nhiều thành phần chất tốt cho sức khỏe, cơ thể của con người. Nó đặc biệt tốt cho những ai mắc bệnh liên quan đến xương, khớp.<br /> <strong>Vi cá </strong>thường được chế biến thành những sợi cước cá sạch trắng. Nó thường được dùng nấu súp với các thực phẩm như: cua, thịt gà, hải sâm... Đây được coi là món ăn quý hiếm, đắt tiền rất được ưa chuộng tại Mỹ và các nước châu Á như Hong Kong, Nhật Bản.</p>\r\n<p><img class=\"alignnone size-full wp-image-5897\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/sup_vi_ca_map.jpg\" alt=\"Súp vi cá mập\" width=\"784\" height=\"532\" /></p>\r\n<p style=\"text-align: center;\"><em>Súp vi cá mập</em></p>\r\n<h2>Thành phần chính</h2>\r\n<p>Trong vi cá mập có <em>chất chondroitin</em>. Chất này có khả năng gây ra ức chế những loại men gây hại cho sụn khớp. Do đó, sử dụng vi cá mập giúp bảo vệ xương khớp. Trong y học, các bác sĩ cũng khuyên người mắc bệnh xương khớp nên dùng nó thường xuyên giúp cải thiện chức năng sụn khớp, đẩy lùi bệnh. Không chỉ có vậy, chondroitin còn có thể đẩy lùi bệnh ung thư, gây ức chế mầm bệnh, nuôi dưỡng tế bào giác mạc để giữ thị lực được tốt nhất, đồng thời nó còn hỗ trợ tim mạch cũng như thần kinh.</p>\r\n<p>Ngoài ra, còn có canxi, phootpho, kẽm… là những chất cực kỳ quan trọng đối với sức khỏe của con người.</p>\r\n<h2>Vi cá mập có tác dụng gì?</h2>\r\n<ul>\r\n<li>Bảo vệ xương luôn chắc khỏe, tái tạo mô sụn và cấu trúc xương. Đồng thời bổ xung các chất dinh dưỡng cần thiết, tạo dịch và làm giảm các cơn đau do thoái hóa khớp.</li>\r\n<li>Ngăn chặn sự hình thành và phát triển của tế bào ung thư.</li>\r\n<li>Nuôi dưỡng tế bào giác mạc giúp mắt khỏe hơn, sáng hơn, không bị khô mắt, giảm thị lực.</li>\r\n<li>Cung cấp canxin giúp xương chắc khỏe, răng và tóc chắc hơn.</li>\r\n<li>Giúp chị em phụ nữ có được làn da mịn màng, căng tràn sức sống.</li>\r\n<li>Chống lại quá trình lão hóa.</li>\r\n</ul>\r\n<h2>Mua vi cá mập ở đâu? Giá bao nhiêu?</h2>\r\n<p>Do nhu cầu của khách hàng sử dụng vi cá mập ngày càng tăng bởi chính những công dụng tuyệt vời mà nó mang lại. Vì vậy trên thị trường cũng xuất hiện nhiều địa chỉ bán thực phẩm này. Tuy nhiên, giá cả mỗi nơi một khác, bởi vậy rất nhiều người thắc mắc về giá của nó bao nhiêu?</p>\r\n<p><strong>Giá vi cá mập được cung cấp bởi tủ thuốc nam</strong></p>\r\n<ul>\r\n<li><strong>Vi cá mập loại 1 ( đã qua sơ chế, còn nguyên vây, 100g/vây )</strong>: 1.200.000 VNĐ/ 100g.</li>\r\n<li><strong>Vi cá mập loại 2 ( 2 vây/100g)</strong>: 1.100.000 VNĐ / 100G.</li>\r\n<li><strong>Cước vi cá mập loại bông to ( 10 bông/100g)</strong>: 1350.000 VNĐ.</li>\r\n<li><strong>Vi cá mập khô</strong>: 4.500.00 VNĐ / 1kg.</li>\r\n<li><strong>Vi cá mập tươi</strong>: giá liên hệ.</li>\r\n</ul>\r\n<p>Hiện nay trên thị trường cũng đã xuất hiện sản phẩm thuốc vi cá mập. Nó vẫn bảo toàn được trọn vẹn công dụng của thực phẩm này. Không chỉ có thế, thuốc còn dễ dàng vận chuyển đi nhiều nơi với thời gian dài, bảo quản thuận lợi hơn và giá thành cũng rẻ hơn một chút.</p>\r\n<h2>Cách phân biệt vi cá mập thật giả</h2>\r\n<ul>\r\n<li>Vi cá giả không chịu được nhiệt nóng, nấu nhanh mềm, ăn vị giống như ăn thạch, cắn là đứt. Vi cá thật có tính đàn hồi, ăn vị thơm ngon.</li>\r\n<li>Vi cá giả tách sợi để trên bàn, dùng ngón tay xoa có vật dạng như cơm rơi xuống. Vi cá thật là chất xơ đàn hồi.</li>\r\n<li>Vi cá giả kéo hai đầu rất dễ đứt. Vi cá thật dùng lực mạnh mới đứt.</li>\r\n<li>Khi ngửi, vi cá thật có mùi tanh. Vi cá giả không có mùi tanh của cá.</li>\r\n<li>Nước ngâm vi cá thật rất thanh trong. Nước ngâm vi cá giả có thể có chất màu vàng chảy ra.</li>\r\n</ul>',	'2018-05-05 14:59:15',	1,	1,	',1,',	1,	NULL,	'28',	NULL,	NULL,	'vi-ca-map',	NULL,	1,	NULL,	NULL,	12),
(26,	'Sâm tươi Hàn Quốc loại 3 củ/1kg',	'Sâm tươi Hàn Quốc loại 3 củ/1kg được nhập khẩu trực tiếp từ Hàn Quốc, uy tín chất lượng giá rẻ nhất thị trường.',	'Sâm tươi Hàn Quốc loại 3 củ/kg uy tín, giá rẻ nhất thị trường',	'<strong>Sâm tươi Hàn Quốc loại 3 củ/1kg </strong>nhập khẩu trực tiếp từ Hàn Quốc rất thích hợp để bồi bổ sức khẻo, làm quà biếu bạn bè, người thân.',	4200,	4000,	'b5491-sam_tuoi_han_quoc_loai_3_cu_1kg.jpg',	'2018-05-02 11:11:53',	'<p><strong>Sâm tươi Hàn Quốc loại 3 củ/kg </strong>là loại sâm tươi có nhiều rễ, củ sâm đẹp và đủ tuổi. Sâm được đóng trong hộp sang trọng, chất lượng đã được bộ ý tế kiểm định. Sâm tươi Hàn Quốc 3 củ/1kg sẽ là món quà ý nghĩa và giá trị để bạn tặng cho bạn bè, người thân, cấp trên của mình.</p>\r\n<p><img class=\"alignnone size-full wp-image-5902\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Bình rượu sâm tươi Hàn Quốc\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/binh_ruou_sam_tuoi_han_quoc.jpg\" alt=\"Bình rượu sâm tươi Hàn Quốc\" width=\"656\" height=\"734\" /></p>\r\n<p style=\"text-align: center;\"><em>Bình rượu sâm tươi Hàn Quốc</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 3 củ/1kg</h2>\r\n<ul>\r\n<li>Ngăn chặn sự phát triển của các tế bào ung thư.</li>\r\n<li>Điều hòa tuần hoàn và giảm Cholesterol trong máu.</li>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Chống lão hóa, làm cho da tươi sáng, mịn màng.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Tăng cường trí nhớ, khả năng tập trung.</li>\r\n<li>Điều hòa lượng đường trong máu, rất tốt cho người bị bênh tiểu đường.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n</ul>\r\n<ul class=\"wc-tabs tabs-nav\">\r\n<li class=\"active et-opened\"><a id=\"tab_description\" class=\"tab-title opened\" href=\"http://tuthuocnam.com/san-pham/nhan-sam-tuoi-han-quoc-loai-8-cu-1kg.html#tab_description\">MÔ TẢ</a></li>\r\n<li><a id=\"tab_reviews\" class=\"tab-title \" href=\"http://tuthuocnam.com/san-pham/nhan-sam-tuoi-han-quoc-loai-8-cu-1kg.html#tab_reviews\">ĐÁNH GIÁ (0)</a></li>\r\n</ul>\r\n<div id=\"content_tab_description\" class=\"tab-content tab-description\">\r\n<div class=\"tab-content-inner\">\r\n<div class=\"tab-content-scroll\">\r\n<blockquote>\r\n<p><strong>Nhân sâm tươi Hàn Quốc</strong> loại 8 củ/1kg đều là nhân sâm 6 năm tuổi với kích thước to bé khác nhau. Đây là loại sâm tươi Hàn Quốc được nhiều người sử dụng nhất. Chúng có tác dụng bồi bổ toàn diện cho cơ thể, bổ máu, giúp cơ thể nhanh chóng phục hồi sức khỏe. Là “thượng phẩn dinh dưỡng” của đông y.</p>\r\n</blockquote>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 8 củ/1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Cải thiện làn da, giúp da trở nên trẻ trung và mịn màng hơn.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<ul>\r\n<li>Nhân sâm tươi Hàn quốc 6 năm tuổi thích hợp với mọi đối tượng. Mọi người sử dụng sâm với mục đích bổi bổ cơ thể, phòng chống stress làm cơ thể suy yếu.</li>\r\n<li>Người làm việc nhiều, tinh thần mệt mỏi nên dùng sâm tươi Hàn quốc thường xuyên giúp duy trì trạng thái tỉnh táo và chống mệt mỏi.</li>\r\n<li>Người bị mắc bệnh ung thư thì không nên sử dụng nhân sâm Hàn Quốc.</li>\r\n</ul>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>\r\n</div>\r\n</div>\r\n</div>',	'2018-05-05 10:03:58',	1,	1,	',1,',	1,	NULL,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-3-cu-1kg',	NULL,	NULL,	NULL,	NULL,	35),
(27,	'Sâm tươi Hàn Quốc loại 6 củ/1kg',	'Sâm tươi Hàn Quốc loại 6 củ 1kg nhập khẩu trực tiếp từ Hàn Quốc giá tốt nhất thị trường. Thượng phẩm dinh dưỡng cho sức khỏe của mọi người.',	'Sâm tươi Hàn Quốc loại 6 củ/1kg giá rẻ nhất thị trường',	'<strong>Sâm tươi Hàn Quốc loại 6 củ 1kg </strong>là thượng phẩm dinh dưỡng cho mọi người. Sâm giúp bồi bổ và tăng cường sức khỏe cho người già, người ốm mệt, người lao động nặng, căng thẳng do áp lực công việc và cuộc sống.',	2600,	NULL,	'c8e4a-sam_tuoi_han_quoc_loai_6_cu_1kg.jpg',	'2018-05-02 11:18:59',	'<p><strong>Sâm tươi Hàn Quốc loại 6 củ/kg</strong> là những củ sâm đủ được chăm sóc cẩn thận ở Quận Geumsan thuộc tỉnh Chungcheong Nam. Nhân Sâm được trồng quận Geumsan là nơi cho chất lượng sâm tốt nhất, uy tín trên toàn thế giới. Sâm dược phát triển trong điều kiện tự nhiên với dòng nước trong lành tại hơn 3000 đồi, núi màu mỡ. Điều kiện tự nhiên đặc biệt đã giúp các cây nhân sâm tích tụ được các dưỡng chất cần thiết cự tốt cho sức khỏe.</p>\r\n<p><img class=\"alignnone size-full wp-image-5906\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/sam_tuoi_han_quoc_loai_6_cu.jpg\" alt=\"Sâm tươi Hàn Quốc loại 6 củ 1kg\" width=\"500\" height=\"334\" /></p>\r\n<p style=\"text-align: center;\"><em>Sâm tươi Hàn Quốc loại 6 củ 1kg</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 6 củ/1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Ngăn chặn sự phát triển của các tế bào ung thư.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Chống lão hóa, làm cho da tươi sáng, mịn màng.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<p>Sâm tươi Hàn quốc thích hợp cho những người có sức khỏe yếu, biếng ăn, ngủ kém, kiệt sức, stress, bị áp lực trong công việc, trẻ em ốm yếu, kén ăn, suy dinh dưỡng, người già yếu cần bồi bổ kéo dài tuổi thọ tăng cường trí nhớ.</p>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>',	'2018-05-05 14:58:10',	1,	1,	',1,',	1,	NULL,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-6-cu-1kg',	NULL,	NULL,	NULL,	NULL,	13),
(28,	'Sâm tươi Hàn Quốc loại 4 củ/1kg',	'Sâm tươi Hàn quốc loại 4 củ 1 kg giúp bồ bổ sức, khỏe tăng cường trí nhớ, bổ khí tráng dương, tăng cường sinh lực, bảo về tim mạch.',	'Sâm tươi Hàn Quốc loại 4 củ/1kg quà tặng ý nghĩa cho người thân',	'Sâm tươi Hàn Quốc loại 4 củ 1kg được nhập khẩu trực tiếp từ Hàn Quốc. Tủ thuốc nam cam kết tuyển chọn những củ sâm tốt nhất cho quý khách.',	3500,	NULL,	'9b906-sam_tuoi_han_quoc_loai_4_cu_1_kg.jpg',	'2018-05-02 11:27:39',	'<p><strong>Nhân sâm</strong> đứng đầu trong 4 vị thuốc quý của đông y: sâm, nhung, quế, phụ. Những công dụng tuyệt vời của nó đã được khoa học chứng minh. Từ xa xưa nhân sâm được biết đến là vật phẩm quý giá, dành cho vua, chúa và các tầng lớp thượng lưu.</p>\r\n<p><strong>Sâm tươi Hàn Quốc loại 4 củ 1 kg </strong>với rất nhiều tác dụng tuyệt vời sẽ là món quà vô cùng ý nghĩa cho gia đình, người thân và bạn bè.</p>\r\n<p><img class=\"alignnone size-full wp-image-5910\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Sâm tươi Hàn Quốc loại 4 củ 1 kg\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/sam_tuoi_han_quoc_loai_4_cu.jpg\" alt=\"Sâm tươi Hàn Quốc loại 4 củ 1 kg\" width=\"265\" height=\"190\" /></p>\r\n<p style=\"text-align: center;\"><em>Sâm tươi Hàn Quốc loại 4 củ 1kg</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 4 củ/1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Ngăn chặn sự phát triển của các tế bào ung thư.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Chống lão hóa, làm cho da tươi sáng, mịn màng.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<p>Sâm tươi Hàn quốc thích hợp cho những người có sức khỏe yếu, biếng ăn, ngủ kém, kiệt sức, stress, bị áp lực trong công việc, trẻ em ốm yếu, kén ăn, suy dinh dưỡng, người già yếu cần bồi bổ kéo dài tuổi thọ tăng cường trí nhớ.</p>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>',	'2018-05-05 14:57:42',	1,	1,	',1,',	1,	NULL,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-4-cu-1-kg',	NULL,	NULL,	NULL,	NULL,	8),
(29,	'Sâm tươi Hàn Quốc loại 5 củ 1kg',	'Sâm tươi Hàn Quốc loại 5 củ 1kg giúp tăng cường trí nhớ, bồ bổ cơ thể, nâng cao khả năng miễn dịch, giảm căng thẳng mệt mỏi.',	'Sâm tươi Hàn Quốc loại 5 củ 1kg - Quà biếu cao cấp.',	'<strong>Sâm tươi Hàn Quốc loại 5 củ 1kg</strong> giúp tăng cường trí nhớ, bồ bổ cơ thể, nâng cao khả năng miễn dịch, giảm căng thẳng mệt mỏi.',	2800,	NULL,	'93cd8-sam_tuoi_han_quoc_loai_5_cu_1_kg.jpg',	'2018-05-02 11:31:28',	'<p><strong>Sâm tươi Hàn quốc loại 5 củ 1kg</strong> với nhiều thành phần tự nhiên quý hiếm cực tốt cho sức khỏe, được ưu chuộng trên toàn thế giới. Sâm được đóng trong hộp 5 củ to, đẹp, nhiều rễ rất thích hợp bồ bổ cơ thể, làm quà tặng sếp, bạn bè, người thân. Nó sẽ là món quà rất ý nghĩa trong các dịp lễ, tết.</p>\r\n<p><img class=\"alignnone size-full wp-image-5914\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/sam_tuoi_han_quoc_loai_5_cu.jpg\" alt=\"Sâm tươi Hàn Quốc loại 5 củ 1 kg\" width=\"526\" height=\"349\" /></p>\r\n<p style=\"text-align: center;\"><em>Sâm tươi Hàn Quốc loại 5 củ 1kg</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 5 củ/1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Ngăn chặn sự phát triển của các tế bào ung thư.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Chống lão hóa, làm cho da tươi sáng, mịn màng.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<p>Sâm tươi Hàn quốc thích hợp cho những người có sức khỏe yếu, biếng ăn, ngủ kém, kiệt sức, stress, bị áp lực trong công việc, trẻ em ốm yếu, kén ăn, suy dinh dưỡng, người già yếu cần bồi bổ kéo dài tuổi thọ tăng cường trí nhớ.</p>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>',	'2018-05-05 14:56:45',	1,	1,	',1,',	1,	NULL,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-5-cu-1-kg',	NULL,	NULL,	NULL,	NULL,	3),
(30,	'Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg',	'Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg nhập khẩu trực tiếp từ Hàn Quốc. Tủ thuốc nam tự tin là đơn vị cung cấp sâm tươi hàng đầu.',	'Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg uy tín, chất lượng.',	'Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg được nhập khẩu trực tiếp từ Hàn Quốc. Sản phẩm bổ dưỡng cho gia đình, làm quà cho người thân, bạn bè, hay sếp.',	1700,	NULL,	'4bd5f-sam_tuoi_han_quoc_loai_14_18_cu_1kg.jpg',	'2018-05-02 11:34:57',	'<p><strong>Nhân Sâm tươi Hàn Quốc 14-18 củ 1kg</strong> được trồng tại quận Geumsan Hàn Quốc. Nơi có khí hậu và thổ nhưỡng rất thích hợp để sâm phát triển và cho chất lượng tốt nhất. Chúng tôi đã tuyển trọn những củ sâm tốt nhất cho bạn, đảm về chất lượng cũng như giá cả.</p>\r\n<p><img class=\"size-full wp-image-5919 aligncenter\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/sam_tuoi_han_quoc_14_18.jpg\" alt=\"Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg\" width=\"400\" height=\"354\" /></p>\r\n<p style=\"text-align: center;\"><em>Sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 14 đến 18 củ 1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Ngăn chặn sự phát triển của các tế bào ung thư.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Chống lão hóa, làm cho da tươi sáng, mịn màng.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<p>Sâm tươi Hàn quốc thích hợp cho những người có sức khỏe yếu, biếng ăn, ngủ kém, kiệt sức, stress, bị áp lực trong công việc, trẻ em ốm yếu, kén ăn, suy dinh dưỡng, người già yếu cần bồi bổ kéo dài tuổi thọ tăng cường trí nhớ.</p>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>',	'2018-05-05 14:52:12',	1,	1,	',1,',	1,	NULL,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-14-den-18-cu-1kg',	NULL,	NULL,	NULL,	NULL,	1),
(31,	'Sâm tươi Hàn Quốc loại 10 đến 12 củ 1kg',	'Sâm tươi Hàn quốc loại 10 đến 12 củ 1kg quà tặng ý nghĩa cho gia đình, bạn bè, người thân, sếp... trong mỗi dịp lễ tết.',	'Sâm tươi Hàn Quốc loại 10 đến 12 củ 1 kg - Quà biếu ý nghĩa.',	'<strong>Sâm tươi Hàn quốc loại 10 đến 12 củ 1kg</strong> quà tặng ý nghĩa cho gia đình, bạn bè, người thân, sếp... trong mỗi dịp lễ tết.',	1980,	NULL,	'd6b37-sam_tuoi_han_quoc_loai_10_12_cu_1kg.jpg',	'2018-05-02 11:39:51',	'<p><strong>Sâm tươi Hàn Quốc từ 10 đến 12 củ 1kg</strong> hiện nay đang là sản phẩm được rất nhiều người lựa chọn và sử dụng để bồi bổ sức khỏe cũng như làm quà biếu, tặng trong các dịp lễ tết. Hãy cùng chúng tôi tìm hiểu về chúng:</p>\r\n<p><img class=\"alignnone size-full wp-image-5923\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/sam_tuoi_han_quoc_loai_10_12.jpg\" alt=\"Sâm tươi Hàn Quốc loại 10 đến 12 củ 1kg\" width=\"412\" height=\"303\" /></p>\r\n<p style=\"text-align: center;\"><em>Sâm tươi Hàn Quốc loại 10 đến 12 củ 1kg</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 10 đến 12 củ 1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Cải thiện làn da, giúp da trở nên trẻ trung và mịn màng hơn.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<ul>\r\n<li>Nhân sâm tươi Hàn quốc 6 năm tuổi thích hợp với mọi đối tượng. Mọi người sử dụng sâm với mục đích bổi bổ cơ thể, phòng chống stress làm cơ thể suy yếu.</li>\r\n<li>Người làm việc nhiều, tinh thần mệt mỏi nên dùng sâm tươi Hàn quốc thường xuyên giúp duy trì trạng thái tỉnh táo và chống mệt mỏi.</li>\r\n<li>Người bị mắc bệnh ung thư thì không nên sử dụng nhân sâm Hàn Quốc.</li>\r\n</ul>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>\r\n<h2>Cách nhận biết Nhân sâm Hàn Quốc thật</h2>\r\n<ul>\r\n<li><strong>Nhân sâm Hàn Quốc</strong> có hình dáng giống người.</li>\r\n<li>Sâm vẫn còn đất bám xung quanh.</li>\r\n<li>Phần trên đầu củ sâm có dấu vết tối thiểu của hai lần rụng hoa.</li>\r\n<li>Phần thân nhìn rất rắn chắc.</li>\r\n<li>Có nhiều rễ nhỏ chỉ bám vào chân sâm chứ không bám nhiều vào thân củ.</li>\r\n<li>Mùi của nhân sâm Hàn Quốc rất thơm.</li>\r\n<li>Ứng với mỗi vùng đất khác nhau , củ sâm sẽ có màu khác nhau.</li>\r\n</ul>\r\n<h2>Nhân sâm Hàn Quốc giả</h2>\r\n<ul>\r\n<li>Một số người bán đã dùng đậu đũa dại, thương lục, niễng rừng, hoa sơn sâm, trong đó sâm đất hay được dùng nhất để làm giả nhân sâm Hàn Quốc.</li>\r\n<li>Sâm đất có hình nón hoặc hình thoi. Khi mới đào lên, bề mặt có màu đen nâu, thô ráp, nhiều vằn. Khi đã sơ chế, bề mặt có vằn rúm màu vàng nâu, thô ráp, rất giòn, dễ bẻ gãy, có chất keo trong mờ, vị ngọt.</li>\r\n<li>Thương lục có hình trụ, khi cắt ra có màu nâu vàng đến nâu đen, không phẳng, có mùi tanh, vị đắng và cay chua.</li>\r\n</ul>\r\n<p><img class=\"size-full wp-image-5685 aligncenter\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Phân biệt thật giả sâm Hàn Quốc\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/phan-biet-sam-han-quoc-that-gia.jpg\" alt=\"Phân biệt thật giả sâm Hàn Quốc\" width=\"320\" height=\"203\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh phân biệt sâm Hàn Quốc thật giả.</em></p>\r\n<h2> </h2>',	'2018-05-05 14:36:50',	1,	1,	',1,',	1,	NULL,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-10-den-12-cu-1kg',	NULL,	NULL,	NULL,	NULL,	1),
(32,	'Sâm tươi Hàn Quốc Loại 2 củ 1kg',	'Sâm tươi Hàn Quốc loại 2 củ 1kg là loại sâm cao cấp củ to chứa nhiều hàm lượng saponin cực tốt cho sức khỏe.',	'Sâm tươi Hàn Quốc Loại 2 củ 1kg - Quà biếu cao cấp.',	'<strong>Sâm tươi Hàn Quốc loại 2 củ 1kg</strong> là loại sâm cao cấp củ to chứa nhiều hàm lượng saponin cực tốt cho sức khỏe, rất thích hợp để làm quà biếu cao cấp và ý nghĩa.',	5490,	NULL,	'89026-sam_tuoi_han_quoc_loai_2cu_1kg.jpg',	'2018-05-02 11:46:42',	'<p><strong>Sâm tươi Hàn Quốc loại 2 củ/kg</strong> là loại sâm cao cấp được chọn lọc từ những củ sâm to, có chứa nhiều dưỡng chất, nhất là hàm lượng saponin giúp bồi bổ sức khỏe, hạn chế mệt mỏi suy nhược và nâng cao tuổi thọ. Sâm được nhập trực tiếp từ Hàn Quốc, đóng trong hộp gồm 2 củ to nhìn rất sang trọng. Nó sẽ là món quà cực kỳ ý nghĩa trong các dịp lễ, tết.</p>\r\n<p><img class=\"alignnone size-full wp-image-5928\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/binh_ruou_sam_tuoi_han_quoc_2_cu.jpg\" alt=\"Bình rượu sâm tươi Hàn Quốc 2 củ\" width=\"300\" height=\"462\" /></p>\r\n<p style=\"text-align: center;\"><em>Bình rượu sâm tươi Hàn Quốc 2 củ</em></p>\r\n<h2>Tác dụng của sâm tươi Hàn Quốc loại 2 củ 1kg</h2>\r\n<ul>\r\n<li>Bồ bổ toàn diện cho cơ thể, giúp cơ thể phục hồi nhanh chóng.</li>\r\n<li>Làm hạ cholesterol và triglycerid trong máu, ngăn chặn được bệnh xơ vữa động mạch và ổn định huyết áp.</li>\r\n<li>Bổ sung lượng canxi và kích thích trí não hoạt động giúp tăng cường trí nhớ của con người.</li>\r\n<li>Cải thiện làn da, giúp da trở nên trẻ trung và mịn màng hơn.</li>\r\n<li>Tăng cường sinh lí, bổ thận tráng dương.</li>\r\n<li>Giúp ăn ngon ngủ tốt, tạo sự tỉnh táo.</li>\r\n<li>Bổ máu, tăng cường hoạt động của hệ thần kinh.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng Sâm tươi Hàn Quốc</h2>\r\n<ul>\r\n<li>Nhân sâm tươi Hàn quốc 6 năm tuổi thích hợp với mọi đối tượng. Mọi người sử dụng sâm với mục đích bổi bổ cơ thể, phòng chống stress làm cơ thể suy yếu.</li>\r\n<li>Người làm việc nhiều, tinh thần mệt mỏi nên dùng sâm tươi Hàn quốc thường xuyên giúp duy trì trạng thái tỉnh táo và chống mệt mỏi.</li>\r\n<li>Người bị mắc bệnh ung thư thì không nên sử dụng nhân sâm Hàn Quốc.</li>\r\n</ul>\r\n<h2>Cách bảo quản Nhân Sâm tươi</h2>\r\n<ul>\r\n<li><strong>Bảo quản trong tủ lạnh</strong>: Khi mua nhân Sâm tươi Hàn Quốc khi mua về, bạn rửa sạch để thật ráo nước rồi cuốn trong tờ giấy cho vào ngăn mát của tủ lạnh. Khi sử dụng, lấy từng củ ra thái lát mỏng rồi lại cất vào tủ lạnh sâm sẽ không bị hỏng.</li>\r\n<li><strong>Sấy khô</strong>: Bạn có thể sấy khô Nhân Sâm ở nhiệt độ từ 40 đến 70 độ. Sau đó bỏ Nhân Sâm Hàn Quốc vào trong lọ có chất hút ẩm, bịt kín lại. Cứ 1 tháng ta lại thay chất hút ẩm một lần, cách này tuy hiệu quả nhưng phải làm đều đặn thường xuyên. Nếu quên không thay chất hút ẩm nhân sâm dễ bị mốc.</li>\r\n<li><strong>Nhân Sâm ngâm mật ong</strong>: Cắt sâm thành các miếng nhỏ 1-3 gam, bỏ vào lọ rộng miệng, sạch, khô, có nắp đậy kín… Sau đó, đổ mật ong ngập hết các miếng sâm rồi đậy nắp lại. Mật ong vừa là chất bảo quản chống mốc, mọt vừa giữ cho Nhân Sâm không bị biến chất do độ ẩm của không khí, lại vừa là thuốc bổ.</li>\r\n<li><strong>Nhân Sâm ngâm rượu</strong>: Dùng Nhân Sâm tươi Hàn Quốc 6 năm tuổi ngâm với rượu nếp nguyên chất. Ngâm khoảng 3 tháng, rượu có mùi hương của Sâm tươi, rất thơm là dùng được.</li>\r\n</ul>\r\n<h2><strong>Cách sử dụng Nhân Sâm tươi Hàn Quốc</strong></h2>\r\n<ul>\r\n<li>Pha trà uống.</li>\r\n<li>Sâm tán bột.</li>\r\n<li>Ngậm tan.</li>\r\n<li>Sắc uống.</li>\r\n<li>Nấu cháo ăn.</li>\r\n<li>Sâm hầm thịt gà.</li>\r\n<li>Ngâm rượu.</li>\r\n<li>Ngâm mật ong.</li>\r\n</ul>\r\n<h2>Cách nhận biết Nhân sâm Hàn Quốc thật</h2>\r\n<ul>\r\n<li><strong>Nhân sâm Hàn Quốc</strong> có hình dáng giống người.</li>\r\n<li>Sâm vẫn còn đất bám xung quanh.</li>\r\n<li>Phần trên đầu củ sâm có dấu vết tối thiểu của hai lần rụng hoa.</li>\r\n<li>Phần thân nhìn rất rắn chắc.</li>\r\n<li>Có nhiều rễ nhỏ chỉ bám vào chân sâm chứ không bám nhiều vào thân củ.</li>\r\n<li>Mùi của nhân sâm Hàn Quốc rất thơm.</li>\r\n<li>Ứng với mỗi vùng đất khác nhau , củ sâm sẽ có màu khác nhau.</li>\r\n</ul>\r\n<h2>Nhân sâm Hàn Quốc giả</h2>\r\n<ul>\r\n<li>Một số người bán đã dùng đậu đũa dại, thương lục, niễng rừng, hoa sơn sâm, trong đó sâm đất hay được dùng nhất để làm giả nhân sâm Hàn Quốc.</li>\r\n<li>Sâm đất có hình nón hoặc hình thoi. Khi mới đào lên, bề mặt có màu đen nâu, thô ráp, nhiều vằn. Khi đã sơ chế, bề mặt có vằn rúm màu vàng nâu, thô ráp, rất giòn, dễ bẻ gãy, có chất keo trong mờ, vị ngọt.</li>\r\n<li>Thương lục có hình trụ, khi cắt ra có màu nâu vàng đến nâu đen, không phẳng, có mùi tanh, vị đắng và cay chua.</li>\r\n</ul>\r\n<p><img class=\"size-full wp-image-5685 aligncenter\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Phân biệt thật giả sâm Hàn Quốc\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/phan-biet-sam-han-quoc-that-gia.jpg\" alt=\"Phân biệt thật giả sâm Hàn Quốc\" width=\"320\" height=\"203\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh phân biệt sâm Hàn Quốc thật giả.</em></p>\r\n<p> </p>',	'2018-05-05 14:36:27',	1,	1,	',1,',	1,	1,	'18,19',	NULL,	NULL,	'sam-tuoi-han-quoc-loai-2-cu-1kg',	NULL,	NULL,	NULL,	NULL,	20),
(33,	'Ba kích tím khô',	'Ba kích tím nguyên chất một loại thảo dược quý hiếm, giúp bổ thận, tráng dương, cường gân cốt, trừ phong thấp.',	'Ba kích tím khô nguyên chất - Nam dược quý hiếm',	'<strong>Ba kích tím nguyên chất</strong> một loại thảo dược quý hiếm, giúp bổ thận, tráng dương, cường gân cốt, trừ phong thấp.',	790,	NULL,	'4f2ea-ba_kich_tim.jpg',	'2018-05-02 11:49:59',	'<p><strong>Ba Kích</strong>(dây ruột già) là thảo dược thân leo sống chủ yếu ở các vùng núi phía bắc: Lạng Sơn, Hà Giang, Phú Thọ, Vĩnh Phúc, Hòa Bình… Nó có thể trồng hoặc mọc tự nhiên, sau khi trồng ba năm thì có thể thu hoạch. Từ xưa Đông Y đã dùng rễ cây Ba Kích để làm thuốc. Có rất nhiều bài thuốc dân gian từ cây Ba Kích được truyền tụng như Ba Kích ngâm rượu, Ba Kích ngâm kỉ tử, Ba Kích nấu với cam thảo.</p>\r\n<p><img class=\"alignnone size-full wp-image-5952\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/ba_kich_tim_nguyen_chat.jpg\" alt=\"Ba kích tím nguyên chất\" width=\"450\" height=\"338\" /></p>\r\n<p style=\"text-align: center;\"><em>Ba kích tím</em></p>\r\n<h2>Thành phần hóa học</h2>\r\n<p>Ba kích có chứa hoạt chất anthraglucozit, phytosterol, acid hữu cơ, đường, nhựa, 1 chút tinh dầu, đặc biệt rễ ba kích tươi có chứa nhiều Vitamin C (Ba kích khô không có).</p>\r\n<h2>Tác dụng của ba kích tím</h2>\r\n<ul>\r\n<li>Bổ thận, tráng dương, khỏe gân cốt, trừ phong thấp.</li>\r\n<li>Hạ huyết áp, tăng sức dẻo dai, tăng sức đề kháng của cơ thể.</li>\r\n<li>Tăng cường chức năng sinh lý cho nam giới.</li>\r\n<li>Kéo dài thời gian quan hệ, điều trị bệnh xuất tinh sớm.</li>\r\n<li>Điều trị chứng di mộng tinh ở nam giới.</li>\r\n<li>Điều trị vô sinh cho những nam giới có trạng thái vô sinh tương đối và suy nhược thể lực.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng</h2>\r\n<ul>\r\n<li>Người muốn tăng cường năng lực phòng the, kéo dài thời gian yêu (Dùng cho cả nam giới và nữ giới).</li>\r\n<li>Người bị liệt dương, suất tinh sớm nên dùng ba kích ngâm rượu.</li>\r\n<li>Người mắc chứng di mộng tinh.</li>\r\n<li>Người bình thường dùng rượu ba kích hàng ngày sẽ giúp tăng cường sức khỏe, tăng sức dẻo dai.</li>\r\n<li>Người trung niên và người già dùng rượu ba kích giúp kiện gân cốt, bổ thận dương.</li>\r\n</ul>\r\n<h2>Điều cấm kỵ khi dùng ba kích</h2>\r\n<ul>\r\n<li>Bắt buộc phải bỏ lõi của ba kích.</li>\r\n<li>Người âm hư hỏa vượng (sốt nhẹ về chiều) táo bón không dùng được.</li>\r\n<li>Uống rượu ba kích ta không có cảm giác say, tuy nhiên ta không nên uống quá nhiều (Mỗi ngày nên uống 4-5 ly nhỏ là vừa đủ).</li>\r\n</ul>\r\n<h2>Cách chế biến thành ba kích khô</h2>\r\n<p>Rễ cây đào về cắt bỏ cổ rễ và rễ con, lấy phần củ có đường kính 0,5cm trở lên.<br /> Phơi nắng cho héo rồi dùng chày gỗ đập nhẹ cho bẹp phần thịt (tránh dập nát).<br /> Tiếp tục phơi, sấy khô, thịt sẽ biến thành màu tím hoặc hồng tím. Bỏ lõi gỗ nhỏ bên trong.<br /> Cuối cùng là cắt thành từng đoạn ngắn 10cm và sử dụng.</p>\r\n<h2>Cách chế biến ba kích tươi</h2>\r\n<p>Củ ba kích sau khi thu hái về sẽ được rửa sạch và phơi ráo nước.<br /> Dùng dao khía nhẹ vào phần thịt củ ba kích để lộ ra phần lõi, dùng dao tách phần thịt và rút bỏ lõi.<br /> Phần thịt của ba kích sẽ được dùng làm thuốc (thường để ngâm rượu) bỏ phần lõi, không sử dụng.</p>\r\n<h2>Phân biệt với ba kích giả, ba kích trắng</h2>\r\n<h3>Ba kích tím</h3>\r\n<p><em>Đây là loại ba kích được dùng phổ biến từ xưa do rất tốt cho sức khỏe.</em><br /> Ba kích tím rừng là những củ có dây hơi nhỏ, kích cỡ không đồng đều nhau. Ruột có màu tím ngắt rõ ràng. Hiện nay trên thị trường có rất nhiều loại ba kích có xuất xứ từ Trung Quốc có hình dáng rất đẹp, băt mắt nhưng không còn hoạt chất vì đã bị tách chiết. Đặc biệt là những loại Ba Kích khô, có giá rất rẻ khoảng 200-500k rất khó để cho người tiêu dùng phân biệt. Chúng tôi chỉ bán 1 loại ba kích tím duy nhất, cam kết hoàn lại 200% tiền khi hàng kém chất lượng.</p>\r\n<h3>Ba kích trắng</h3>\r\n<p>Loại này ít dùng, do tác dụng không tốt bằng ba kích tím.<br /> <strong>Cách phân biệt</strong>: Củ có màu vàng nhạt, phần thịt bên trong màu trắng trong, không có sắc tím.<br /> Khi ngâm rượu: rượu không chuyển màu tím (Đặc điểm quan trọng để phân biệt với ba kích tím)</p>',	'2018-05-05 14:35:52',	1,	1,	',1,',	1,	NULL,	'27',	NULL,	NULL,	'ba-kich-tim-kho',	NULL,	NULL,	NULL,	NULL,	15),
(34,	'Nấm lim xanh Kontum',	'Nấm lim xanh Kontum tăng cường hệ miễn dịch, giải độc tế bào gan, hạ mỡ máu, chống lão hóa, kìm hãm sự tăng trưởng của tế bào ung thư...',	'Nấm lim xanh Kontum giúp giải độc gan, hạ mỡ máu',	'<strong>Nấm lim xanh Kontum</strong> tăng cường hệ miễn dịch, giải độc tế bào gan, hạ mỡ máu, chống lão hóa, kìm hãm sự tăng trưởng của tế bào ung thư...',	1290,	NULL,	'7cb8e-nam-lim-xanh-kontum.jpg',	'2018-05-02 12:00:53',	'<p><strong>Nấm Lim Xanh Kontum</strong> thuộc họ Nấm Lim (Ganodermataceae). Thường thấy trên các thân cây lim xanh đã chết trong các khu rừng nguyên sinh ở Trường Sơn – Quảng Nam, Tây Nguyên và Nam Lào. Chúng chứa nhiều hoạt chất quý có lợi cho sức khỏe như Germanium, Ling zhi-8 protein, Polysaccharide, Adenosine, Beta glucan... Những chất này có tác dụng tăng cường hệ miễn dịch, giải độc tế bào gan, hạ mỡ máu, kìm hãm sự phát triển của tế bào ung thư...</p>\r\n<p><img class=\"alignnone size-full wp-image-5956 aligncenter\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://tuthuocnam.com/3rdparty/filemanager/source/sam/nan_lim_xanh_kontum.jpg\" alt=\"Nấm lim xanh tumorong\" width=\"450\" height=\"338\" /></p>\r\n<p style=\"text-align: center;\"><em>Nấm lim xanh Kontum</em></p>\r\n<h2>Công dụng của nấm lim xanh</h2>\r\n<ul>\r\n<li>Giúp bồi bổ cơ thể, tăng cường sức khỏe và sức đề kháng của cơ thể đối với bệnh tật.</li>\r\n<li>Điều trị suy nhược thần kinh, mất ngủ kéo dài, mộng mị, suy nhược sinh lý, di, mộng tinh.</li>\r\n<li>Giảm sự oxy hóa của tế bào nhất là tế bào não giúp nâng cao tuổi thọ. Nấm lim xanh còn giúp hạn chế quá trình lão hóa da ở phụ nữ.</li>\r\n<li>Tăng cường sự chắc khỏe của xương.</li>\r\n<li>Giúp thải độc gan, mát gan.</li>\r\n<li>Ngăn ngừa và điều trị tiểu đường, sỏi thận, bí tiểu tiện, táo bón…</li>\r\n<li>Điều trị rối loạn tiêu hóa, loét dạ dày, tá tràng, viêm đại tràng, kích thích tiêu hóa…</li>\r\n<li>Ngăn ngừa khối u phát triển.</li>\r\n<li>Tăng cường sức khỏe cho những người đang trong quá trình hóa trị, xạ trị: ung thư gan, ung thư vú, ung thư vòm họng, ung thư cổ tử cung…</li>\r\n</ul>\r\n<h2>Cách dùng nấm lim xanh Kontum</h2>\r\n<p>Nấm lim xanh sau khi rửa sạch, cần quan sát xem phần gốc còn dính vỏ hay gỗ cây Lim không? Nếu có cần gọt sạch (vỏ hoặc gỗ Lim rất độc) sau đó phơi cho thật khô.<br /> - <strong>Nấu nước uống</strong>: Thái nhỏ nấm lim nhằm tăng hiệu quả khi nấu thành nước uống. Mỗi lần nấu khoảng 20g-50g nấm cùng 2 lít nước đun sôi với lửa nhỏ khoảng 30 phút còn 1,5 lít. Xong để nguội và có thể sử dụng ngay. Nên nấu vào buổi sáng sớm và uống hết trong ngày. Có thể đun nấm lim cùng các cây thuốc nam hoặc vị thuốc bắc như: táo tàu, cam thảo bắc để dễ uống. Uống liên tục khoảng 1 tuần bắt đầu có cảm giác người khỏe mạnh, ăn ngủ ngon.<br /> - <strong>Hãm trà</strong>: Nghiền nấm lim xanh thành bột, bọc túi vải cho vào ấm hãm uống như uống trà.<br /> - <strong>Ngâm rượu</strong>: Nấm Lim để nguyên chiếc hoặc thái lát, ngâm với rượu mạnh khoảng 20 ngày. Nên uống rượu ngâm nấm Lim vào buổi tối. ngày uống từ 1 đến 2 chén.</p>\r\n<h2>Cách nhận biết nấm lim xanh</h2>\r\n<ul>\r\n<li>Nấm Lim Xanh có màu nâu xám ngả đen hoặc vàng. Mặt trên cây nấm có nhiều chỗ bóng như sừng. Mặt dưới thì mịn như nhung, có thể có lỗ chỗ vết sâu đục nhỏ li ti.</li>\r\n<li>Nấm tự nhiên có hình thức xù xì, xấu xí hơn so với nấm được trồng trong môi trường nhân tạo.</li>\r\n<li>Nấm Lim Xanh có mùi thơm đặc trưng của Nấm Linh chi. Khi nấu thường có vị đắng nhưng nếu hay uống thì thấy vị đắng dễ chịu, không chua. Trong khi đó Nấm giả thì khi mới nấu, nước đầu tiên rất đắng, nhưng đến nước thứ hai thì mùi vị dần mất hẳn.</li>\r\n</ul>',	'2018-05-05 14:34:06',	1,	1,	',1,',	1,	NULL,	'21',	NULL,	NULL,	'nam-lim-xanh-kontum',	NULL,	NULL,	NULL,	NULL,	16),
(35,	'Tam thất bắc',	'Tam Thất Bắc có vị ngọt, hơi đắng, có tính ôn, tác dụng hóa ứ, giúp cầm máu, tiêu sưng, làm giảm đau, tăng cường miễn dịch cho cơ thể.',	'Tam thất bắc - Sâm tam thất - Cây thuốc quý hiếm',	'<strong>Tam Thất Bắc</strong> có vị ngọt, hơi đắng, có tính ôn, tác dụng hóa ứ, giúp cầm máu, tiêu sưng, làm giảm đau, tăng cường miễn dịch cho cơ thể.',	2000,	2100,	'b0b3b-tam_that_bac.jpg',	'2018-05-02 12:04:22',	'<p><strong>Tam thất bắc</strong> là cây thuốc quý hiếm, thuộc họ Nhâm sâm. Tam thất Bắc rất hiếm ở Việt Nam bởi vì cây này chỉ mọc ở các vùng núi có độ cao 1.500m trở lên, cây thích hợp với khí hậu lạnh.<br /> Tam thất Bắc khác hoàn toàn Tam thất nam bởi vậy mà giá của nó đắt gấp 10 lần Tam thất nam. Hiện nay Tam thất nam có giá chỉ từ 300.000đ đến 400.000đ/1Kg. Khi mua hàng bạn cần chú ý tránh nhầm lẫn, kẻo mua phải tam thất nam ít giá trị.</p>\r\n<h2>Thành phần chính</h2>\r\n<p>Các nhà khoa học phát hiện ra rằng trong tam thất bắc có chứa nhiều những dưỡng chất, dược chất rất quý hiếm. Trong đó có các acid amin, sterol, đường, các nguyên tố Fe, Ca và đặc biệt là 2 chất Saponin: Arasaponin A, Arasaponin B thường có trong Nhân Sâm. Tam thất bắc thì không có chứa độc, an toàn khi mà sử dụng.</p>\r\n<h2>Tác dụng của tam thất bắc</h2>\r\n<ul>\r\n<li>Bảo vệ tim chống lại những tác nhân gây loạn nhịp tim.</li>\r\n<li>Cầm máu, tiêu máu, tiêu sưng.</li>\r\n<li>Dịch chiết rễ tam thất có tác dụng gây hưng phấn thần kinh.</li>\r\n<li>Tăng cường miễn dịch cho cơ thể.</li>\r\n<li>Hỗ trợ điều trị Ung thư.</li>\r\n<li>Kích thích hoạt động tình dục mạnh mẽ.</li>\r\n<li>Điều trị thống kinh (đau bụng trước kỳ kinh).</li>\r\n<li>Điều trị bệnh bạch cầu cấp và mạn tính.</li>\r\n<li>Điều trị bệnh băng huyết.</li>\r\n<li>Hỗ trợ điều trị bệnh Đau thắt ngực do bệnh mạch vành.</li>\r\n<li>Giảm đau, chống viêm.</li>\r\n<li>Bổ máu, điều trị chứng thiếu máu, xanh xao.</li>\r\n</ul>\r\n<h2>Đối tượng sử dụng</h2>\r\n<ul>\r\n<li>Bệnh nhân mà bị suy giảm đi chức năng tim, người bị mắc bệnh tim mạch, bệnh về mạch vành, bệnh hở van tim.</li>\r\n<li>Phụ nữ sau khi sinh em bé dùng Tam thất bắc thì có tác dụng giúp bổ máu.</li>\r\n<li>Bệnh nhân bị Ung Thư.</li>\r\n<li>Người hoạt động về trí óc nhiều, hay bị căng thẳng.</li>\r\n<li>Người mà bị chấn thương, bị tụ máu, bầm tím.</li>\r\n<li>Phụ nữ bị chứng thống kinh, băng huyết.</li>\r\n<li>Người mới bị ốm dậy, suy nhược về cơ thể quá sức.</li>\r\n</ul>\r\n<h2>Cách dùng</h2>\r\n<p>Có thể dùng tam thất tươi, rửa sạch, giã và đắp lên trên vết thương. Rửa thật sạch tam thất rồi phơi khô, xay ra bột hoặc là mài với nước uống. Khi sơ chế Củ Tam thất Bắc nên dùng tới bàn chà chải, để cho ráo, ủ rượu cho mềm ra, sắt lát thật mỏng, sấy khô và đựng trong lọ kín ta để dùng dần. Không nên làm theo cách rang tẩm trực tiếp ở trên chảo hoặc là tẩm dược liệu cùng với mỡ gà, rồi mang phơi sấy khô. Khi dùng ta mới thái lát hoặc là tán bột.</p>\r\n<h2>Liều dùng</h2>\r\n<p>Tùy từng bệnh mà có liều dùng khách nhau:</p>\r\n<ul>\r\n<li><strong>Đau bụng ngay trước kỳ kinh nguyệt</strong>: ngày uống 5g bột tam thất trong một lần, có thể mang hầm với cháo hoặc là nấu với nước uống.</li>\r\n<li><strong>Phòng và chữa chứng đau thắt ở ngực</strong>: ngày uống một lượng chuẩn 3-6g bột tam thất.</li>\r\n<li><strong>Chữa thấp tim</strong>: Ngày sử dụng theo chuẩn đó là 3g bột tam thất chia ra ba lần uống. Dùng liên tục trong thời gian 30 ngày.</li>\r\n<li><strong>Người bị chứng đau thắt lưng</strong>: trộn đều bột hồng sâm cùng với bột tam thất mỗi ngày sử dụng 4g, chia ra làm hai lần mỗi lần cách nhau thời gian 12h.</li>\r\n<li><strong>Chữa bạch cầu cấp</strong>: Ngày sử dụng 6g cùng với đương quy(15-30g), xuyên khung(15-30g), xích thược(15-20g) và hồng hoa(8-10g).</li>\r\n<li><strong>Đau thắt ngực do chứng bệnh mạch vành</strong>: ngày uống một lượng chuẩn 3-6g bột tam thất.</li>\r\n</ul>',	'2018-05-02 12:50:14',	1,	1,	',1,',	1,	NULL,	'27',	NULL,	NULL,	'tam-that-bac',	NULL,	NULL,	1,	NULL,	14);

